'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import {
    Box, Paper, Typography, Stack, Button, Card, CardContent, Avatar
} from '@mui/material';
import { User, LogOut, Settings, Home } from 'lucide-react';

export default function DashboardPage() {
    const router = useRouter();
    const [user, setUser] = useState<any>(null);

    useEffect(() => {
        // Get user from localStorage
        const userData = localStorage.getItem('user');
        if (userData) {
            setUser(JSON.parse(userData));
        } else {
            router.push('/login');
        }
    }, [router]);

    const handleLogout = async () => {
        await fetch('/api/auth/logout', { method: 'POST' });
        localStorage.removeItem('auth-token');
        localStorage.removeItem('user');
        router.push('/login');
    };

    if (!user) {
        return (
            <Box display="flex" alignItems="center" justifyContent="center" minHeight="100vh">
                <Typography>Loading...</Typography>
            </Box>
        );
    }

    return (
        <Box sx={{ minHeight: '100vh', bgcolor: '#f8fafc', p: 3 }}>
            {/* Header */}
            <Paper elevation={0} sx={{ p: 3, mb: 3, borderRadius: '16px', border: '1px solid #e2e8f0' }}>
                <Box display="flex" justifyContent="space-between" alignItems="center">
                    <Box display="flex" alignItems="center" gap={2}>
                        <Avatar sx={{ width: 56, height: 56, bgcolor: 'primary.main' }}>
                            <User size={28} />
                        </Avatar>
                        <Box>
                            <Typography variant="h5" fontWeight="bold">
                                Welcome, {user.name}!
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                                {user.email}
                            </Typography>
                        </Box>
                    </Box>
                    <Stack direction="row" spacing={2}>
                        <Button
                            variant="outlined"
                            startIcon={<Home size={18} />}
                            onClick={() => router.push('/')}
                            sx={{ textTransform: 'none', borderRadius: '12px' }}
                        >
                            Home
                        </Button>
                        <Button
                            variant="outlined"
                            startIcon={<LogOut size={18} />}
                            onClick={handleLogout}
                            color="error"
                            sx={{ textTransform: 'none', borderRadius: '12px' }}
                        >
                            Logout
                        </Button>
                    </Stack>
                </Box>
            </Paper>

            {/* Dashboard Content */}
            <Stack spacing={3}>
                <Typography variant="h6" fontWeight="bold">
                    User Dashboard
                </Typography>

                <Box display="grid" gridTemplateColumns={{ xs: '1fr', md: '1fr 1fr 1fr' }} gap={3}>
                    <Card elevation={0} sx={{ border: '1px solid #e2e8f0', borderRadius: '16px' }}>
                        <CardContent>
                            <Box display="flex" alignItems="center" gap={2} mb={2}>
                                <Box p={1.5} bgcolor="#eff6ff" borderRadius="12px">
                                    <User size={24} color="#3b82f6" />
                                </Box>
                                <Typography variant="h6" fontWeight="bold">Profile</Typography>
                            </Box>
                            <Typography variant="body2" color="text.secondary" mb={2}>
                                Manage your account settings and preferences
                            </Typography>
                            <Button
                                variant="outlined"
                                size="small"
                                startIcon={<Settings size={16} />}
                                sx={{ textTransform: 'none', borderRadius: '8px' }}
                            >
                                Edit Profile
                            </Button>
                        </CardContent>
                    </Card>

                    <Card elevation={0} sx={{ border: '1px solid #e2e8f0', borderRadius: '16px' }}>
                        <CardContent>
                            <Typography variant="h6" fontWeight="bold" mb={1}>
                                Account Info
                            </Typography>
                            <Stack spacing={1}>
                                <Box>
                                    <Typography variant="caption" color="text.secondary">
                                        Name
                                    </Typography>
                                    <Typography variant="body2" fontWeight={500}>
                                        {user.name}
                                    </Typography>
                                </Box>
                                <Box>
                                    <Typography variant="caption" color="text.secondary">
                                        Email
                                    </Typography>
                                    <Typography variant="body2" fontWeight={500}>
                                        {user.email}
                                    </Typography>
                                </Box>
                                <Box>
                                    <Typography variant="caption" color="text.secondary">
                                        Role
                                    </Typography>
                                    <Typography variant="body2" fontWeight={500} textTransform="capitalize">
                                        {user.role}
                                    </Typography>
                                </Box>
                            </Stack>
                        </CardContent>
                    </Card>

                    <Card elevation={0} sx={{ border: '1px solid #e2e8f0', borderRadius: '16px' }}>
                        <CardContent>
                            <Typography variant="h6" fontWeight="bold" mb={2}>
                                Quick Actions
                            </Typography>
                            <Stack spacing={1}>
                                <Button
                                    variant="text"
                                    fullWidth
                                    sx={{ justifyContent: 'flex-start', textTransform: 'none' }}
                                    onClick={() => router.push('/')}
                                >
                                    View Website
                                </Button>
                                {user.role === 'admin' && (
                                    <Button
                                        variant="text"
                                        fullWidth
                                        sx={{ justifyContent: 'flex-start', textTransform: 'none' }}
                                        onClick={() => router.push('/admin')}
                                    >
                                        Admin Panel
                                    </Button>
                                )}
                            </Stack>
                        </CardContent>
                    </Card>
                </Box>
            </Stack>
        </Box>
    );
}

'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import {
    Box, Paper, TextField, Button, Typography, Alert, Stack, InputAdornment,
    IconButton, Divider
} from '@mui/material';
import { Mail, Lock, Eye, EyeOff, LogIn } from 'lucide-react';

export default function LoginPage() {
    const router = useRouter();
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            const res = await fetch('/api/auth/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email, password })
            });

            const data = await res.json();

            if (!res.ok) {
                throw new Error(data.error || 'Login failed');
            }

            // Store token in localStorage as backup
            localStorage.setItem('auth-token', data.token);
            localStorage.setItem('user', JSON.stringify(data.user));

            // Redirect based on role and profile completion
            if (data.user.role === 'admin') {
                // Admin users: check if profile is complete
                if (data.isProfileComplete) {
                    router.push('/admin'); // Go to dashboard if profile is complete
                } else {
                    router.push('/admin?section=profile'); // Go to profile if incomplete
                }
            } else {
                // Regular users: check if profile is complete
                if (data.isProfileComplete) {
                    router.push('/user'); // Go to dashboard if profile is complete
                } else {
                    router.push('/user?section=profile'); // Go to profile if incomplete
                }
            }
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box
            sx={{
                minHeight: '100vh',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                background: '#f8fafc',
                p: 2
            }}
        >
            <Paper
                elevation={24}
                sx={{
                    maxWidth: 450,
                    width: '100%',
                    p: 4,
                    borderRadius: '24px',
                    background: '#ffffff',
                }}
            >
                <Stack spacing={3}>
                    {/* Logo/Header */}
                    <Box textAlign="center">
                        <Box
                            sx={{
                                width: 64,
                                height: 64,
                                borderRadius: '16px',
                                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                margin: '0 auto 16px',
                                boxShadow: '0 8px 32px rgba(102, 126, 234, 0.4)'
                            }}
                        >
                            <LogIn size={32} color="white" />
                        </Box>
                        <Typography variant="h4" fontWeight="bold" gutterBottom>
                            Welcome Back
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                            Sign in to access your dashboard
                        </Typography>
                    </Box>

                    {/* Error Alert */}
                    {error && (
                        <Alert severity="error" sx={{ borderRadius: '12px' }}>
                            {error}
                        </Alert>
                    )}

                    {/* Login Form */}
                    <form onSubmit={handleLogin}>
                        <Stack spacing={2.5}>
                            <TextField
                                fullWidth
                                label="Email Address"
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                                InputProps={{
                                    startAdornment: (
                                        <InputAdornment position="start">
                                            <Mail size={20} color="#94a3b8" />
                                        </InputAdornment>
                                    ),
                                }}
                                sx={{
                                    '& .MuiOutlinedInput-root': {
                                        borderRadius: '12px',
                                    }
                                }}
                            />

                            <TextField
                                fullWidth
                                label="Password"
                                type={showPassword ? 'text' : 'password'}
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                                InputProps={{
                                    startAdornment: (
                                        <InputAdornment position="start">
                                            <Lock size={20} color="#94a3b8" />
                                        </InputAdornment>
                                    ),
                                    endAdornment: (
                                        <InputAdornment position="end">
                                            <IconButton
                                                onClick={() => setShowPassword(!showPassword)}
                                                edge="end"
                                                size="small"
                                            >
                                                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                                            </IconButton>
                                        </InputAdornment>
                                    ),
                                }}
                                sx={{
                                    '& .MuiOutlinedInput-root': {
                                        borderRadius: '12px',
                                    }
                                }}
                            />

                            <Button
                                type="submit"
                                variant="contained"
                                size="large"
                                fullWidth
                                disabled={loading}
                                sx={{
                                    borderRadius: '12px',
                                    py: 1.5,
                                    textTransform: 'none',
                                    fontSize: '16px',
                                    fontWeight: 600,
                                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                                    boxShadow: '0 4px 20px rgba(102, 126, 234, 0.4)',
                                    '&:hover': {
                                        boxShadow: '0 6px 30px rgba(102, 126, 234, 0.6)',
                                    }
                                }}
                            >
                                {loading ? 'Signing in...' : 'Sign In'}
                            </Button>
                        </Stack>
                    </form>

                    <Divider sx={{ my: 1 }}>
                        <Typography variant="caption" color="text.secondary">
                            OR
                        </Typography>
                    </Divider>

                    {/* Register Link */}
                    <Box textAlign="center">
                        <Typography variant="body2" color="text.secondary">
                            Don't have an account?{' '}
                            <Button
                                variant="text"
                                onClick={() => router.push('/register')}
                                sx={{ textTransform: 'none', fontWeight: 600 }}
                            >
                                Create Account
                            </Button>
                        </Typography>
                    </Box>
                </Stack>
            </Paper>
        </Box>
    );
}

'use client';

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import {
    Box, Paper, TextField, Button, Typography, Alert, Stack, InputAdornment,
    IconButton, Divider
} from '@mui/material';
import { Mail, Lock, Eye, EyeOff, UserPlus, User } from 'lucide-react';

export default function RegisterPage() {
    const router = useRouter();
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const handleRegister = async (e: React.FormEvent) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            const res = await fetch('/api/auth/register', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ name, email, password })
            });

            const data = await res.json();

            if (!res.ok) {
                throw new Error(data.error || 'Registration failed');
            }

            // Redirect to login page after successful registration
