        description: 'Seamless experiences across all devices, ensuring your site looks perfect on mobile, tablet, and desktop.',
        delay: 'delay-1',
    },
    {
        icon: TrendingUp,
        title: 'Growth Strategy',
        description: 'SEO-optimized structures and user-centric features designed to drive traffic and convert visitors.',
        delay: 'delay-2',
    },
];

const iconMap: { [key: string]: any } = {
    Edit,
    Monitor,
    TrendingUp
};

interface ServicesProps {
    servicesData?: {
        heading: string;
        subHeading: string;
        items: Array<{
            icon: string;
            title: string;
            description: string;
        }>;
    };
}

export default function Services({ servicesData }: ServicesProps) {
    const sectionRef = useRef<HTMLElement>(null);

    const heading = servicesData?.heading || 'Tailored Solutions';
    const subHeading = servicesData?.subHeading || 'What We Do';
    // Use dynamic items or fallback (though fallback logic for icons might need care if structure changes)
    // For simplicity, if servicesData is present, we use it. If not, we could default to empty or keep hardcoded as default.
    // Let's use the provided content or fallback to hardcoded list if "items" is missing.

    // Transform items to include delay if not present in data (or just add it)
    const items = servicesData ? servicesData.items.map((item, index) => ({
        ...item,
        iconComponent: iconMap[item.icon] || Edit, // Fallback icon
        delay: index === 0 ? '' : `delay-${index}` // Add delay class based on index
    })) : services.map(s => ({ ...s, iconComponent: s.icon }));

    useEffect(() => {
        const observer = new IntersectionObserver(
            (entries) => {
                entries.forEach((entry) => {
                    if (entry.isIntersecting) {
                        entry.target.querySelectorAll('.reveal').forEach((el) => {
                            el.classList.add('active');
                        });
                    }
                });
            },
            { threshold: 0.15 }
        );

        if (sectionRef.current) {
            observer.observe(sectionRef.current);
        }

        return () => observer.disconnect();
    }, []);

    // Helper to render heading title parts (assuming first word normal, rest colored, or just simple split?)
    // The previous code had "Tailored <span ...>Solutions</span>"
    // If we want to maintain that style with dynamic string, we might need a convention.
    // For now, let's just render the string. Or split by space?
    // The JSON says "Tailored Solutions". 
    // Let's try to split: last word colored?
    const titleParts = heading.split(' ');
    const lastWord = titleParts.pop();
    const firstPart = titleParts.join(' ');

    return (
        <section
            id="services"
            ref={sectionRef}
            className="px-4 md:px-8 bg-gray-50"
            style={{
                paddingTop: '80px',
                paddingBottom: '80px',
            }}
        >
            <Container maxWidth="xl">
                <div className="reveal text-center md:text-left" style={{ marginBottom: '32px' }}>
                    <span className="block text-xs md:text-sm uppercase tracking-widest mb-2 font-semibold" style={{ color: 'var(--primary-color)' }}>
                        {subHeading}
                    </span>
                    <h2 className="text-3xl sm:text-4xl md:text-5xl font-semibold leading-tight" style={{ marginBottom: '16px' }}>
                        {firstPart} <span style={{ color: 'var(--primary-color)' }}>{lastWord}</span>
                    </h2>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3" style={{ gap: '24px' }}>
                    {items.map((service, index) => {
                        const IconComponent = service.iconComponent;
                        return (
                            <Card
                                key={index}
                                className={`reveal ${service.delay}`}
                                sx={{
                                    background: '#ffffff',
                                    border: '1px solid #e2e8f0',
                                    borderRadius: '16px',
                                    padding: { xs: '20px', md: '24px' },
                                    transition: 'all 0.3s ease',
                                    boxShadow: '0 1px 3px 0 rgb(0 0 0 / 0.1)',
                                    height: '100%',
                                    '&:hover': {
                                        transform: 'translateY(-4px)',
                                        borderColor: 'var(--primary-color)',
                                        boxShadow: '0 10px 20px -5px rgb(0 0 0 / 0.1)',
                                    },
                                }}
                            >
                                <CardContent sx={{ padding: '0 !important' }}>
                                    <div
                                        className="w-12 h-12 md:w-14 md:h-14 flex items-center justify-center rounded-xl mb-4 md:mb-6"
                                        style={{
                                            background: 'rgba(99, 102, 241, 0.1)',
                                            color: 'var(--primary-color)',
                                        }}
                                    >
                                        <IconComponent sx={{ fontSize: { xs: '1.5rem', md: '1.75rem' } }} />
                                    </div>

                                    <h3 className="text-xl md:text-2xl font-semibold" style={{ marginBottom: '12px' }}>{service.title}</h3>
                                    <p className="text-sm md:text-base text-gray-600 leading-relaxed">{service.description}</p>
                                </CardContent>
                            </Card>
                        );
                    })}
                </div>
            </Container>
        </section>
    );
}

import { Box, Typography, Button, IconButton, Badge, Tooltip, Avatar, Popover, List, ListItem, ListItemText, ListItemAvatar, Divider } from '@mui/material';
import { Save, Bell, Search, LogOut, Mail } from 'lucide-react';
import { useState, useEffect } from 'react';

interface AdminHeaderProps {
    title: string;
    description: string;
    onSave?: () => void;
    showSaveButton?: boolean;
    isSaving?: boolean;
    onLogout?: () => void;
}

export default function AdminHeader({ title, description, onSave, showSaveButton = false, isSaving = false, onLogout }: AdminHeaderProps) {
    const [anchorEl, setAnchorEl] = useState<HTMLButtonElement | null>(null);
    const [unreadCount, setUnreadCount] = useState(0);
    const [recentMessages, setRecentMessages] = useState<any[]>([]);

    useEffect(() => {
        // Poll for unread messages (simple implementation)
        const checkNotifications = async () => {
            try {
                const res = await fetch('/api/admin/messages');
                if (res.ok) {
                    const data = await res.json();
                    const unread = data.messages.filter((m: any) => !m.isRead);
                    setUnreadCount(unread.length);
                    setRecentMessages(unread.slice(0, 5)); // Show top 5 unread
                }
            } catch (e) {
                console.error('Notification check failed', e);
            }
        };

        checkNotifications();
        const interval = setInterval(checkNotifications, 60000); // Check every minute
        return () => clearInterval(interval);
    }, []);

    const handleNotificationClick = (event: React.MouseEvent<HTMLButtonElement>) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const open = Boolean(anchorEl);

    return (
        <Box
            component="header"
            sx={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                mb: 4,
                position: 'sticky',
                top: 0,
                zIndex: 5,
                bgcolor: 'rgba(248, 250, 252, 0.8)', // Semi-transparent background
                backdropFilter: 'blur(8px)',
                mx: -4,
                px: 4,
                py: 2,
                borderBottom: '1px solid transparent',
                transition: 'all 0.3s',
            }}
        >
            <Box>
                <Typography variant="h5" fontWeight="800" color="text.primary" sx={{ letterSpacing: '-0.5px' }}>
                    {title}
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mt: 0.5 }}>
                    {description}
                </Typography>
            </Box>

            <Box display="flex" alignItems="center" gap={2}>
                <Box display="flex" gap={1} mr={2} sx={{ display: { xs: 'none', md: 'flex' } }}>
                    <Tooltip title="Search">
                        <IconButton size="small" sx={{ color: 'text.secondary' }}>
                            <Search size={20} />
                        </IconButton>
                    </Tooltip>
                    <Tooltip title="Notifications">
                        <IconButton
                            size="small"
                            sx={{ color: 'text.secondary' }}
                            onClick={handleNotificationClick}
                        >
                            <Badge badgeContent={unreadCount} color="error" max={99}>
                                <Bell size={20} />
                            </Badge>
                        </IconButton>
                    </Tooltip>
                </Box>

                {/* Notifications Popover */}
                <Popover
                    open={open}
                    anchorEl={anchorEl}
                    onClose={handleClose}
                    anchorOrigin={{
                        vertical: 'bottom',
                        horizontal: 'right',
                    }}
                    transformOrigin={{
                        vertical: 'top',
                        horizontal: 'right',
                    }}
                    PaperProps={{
                        sx: { width: 320, borderRadius: '16px', mt: 1.5, boxShadow: '0 4px 20px rgba(0,0,0,0.1)' }
                    }}
                >
                    <Box p={2} borderBottom="1px solid #f1f5f9">
                        <Typography variant="subtitle2" fontWeight="bold">Notifications</Typography>
                    </Box>
                    <List sx={{ p: 0, maxHeight: 300, overflowY: 'auto' }}>
                        {recentMessages.length > 0 ? (
                            recentMessages.map((msg) => (
                                <Box key={msg._id}>
                                    <ListItem alignItems="flex-start" sx={{ px: 2, py: 1.5 }}>
                                        <ListItemAvatar>
                                            <Avatar sx={{ width: 32, height: 32, bgcolor: 'primary.light' }}>
                                                <Mail size={16} />
                                            </Avatar>
                                        </ListItemAvatar>
                                        <ListItemText
                                            primary={
                                                <Typography variant="subtitle2" fontWeight="600" noWrap>
                                                    {msg.subject}
                                                </Typography>
                                            }
                                            secondary={
                                                <Typography variant="caption" color="text.secondary" noWrap display="block">
                                                    {msg.fromUserId?.name} • New Message
                                                </Typography>
                                            }
                                        />
                                    </ListItem>
                                    <Divider component="li" />
                                </Box>
                            ))
                        ) : (
                            <Box p={3} textAlign="center">
                                <Typography variant="body2" color="text.secondary">No new notifications</Typography>
                            </Box>
                        )}
                    </List>
                    <Box p={1.5} borderTop="1px solid #f1f5f9" textAlign="center">
                        <Button
                            size="small"
                            fullWidth
                            sx={{ textTransform: 'none', borderRadius: '8px' }}
                            onClick={() => {
                                handleClose();
                                window.location.href = '/admin?section=messages';
                            }}
                        >
                            View All Messages
                        </Button>
                    </Box>
                </Popover>

                {showSaveButton && onSave && (
                    <Button
                        variant="contained"
                        startIcon={<Save size={18} />}
                        onClick={onSave}
                        disabled={isSaving}
                        sx={{
                            bgcolor: 'var(--primary-color)',
                            px: 3,
                            py: 1,
                            borderRadius: '50px',
                            textTransform: 'none',
                            fontWeight: 600,
                            boxShadow: '0 4px 12px rgba(99, 102, 241, 0.2)',
                            transition: 'all 0.2s',
                            '&:hover': {
                                bgcolor: 'var(--primary-color)',
                                opacity: 0.9,
                                transform: 'translateY(-1px)',
                                boxShadow: '0 6px 16px rgba(99, 102, 241, 0.3)',
                            },
                            '&:active': {
                                transform: 'translateY(0)',
                            },
                            '&:disabled': {
                                bgcolor: 'var(--text-muted)',
                                color: 'white'
                            },
                            mr: 2
                        }}
                    >
                        {isSaving ? 'Saving...' : 'Save Changes'}
                    </Button>
                )}

                <Tooltip title="Logout">
                    <IconButton size="small" onClick={onLogout} sx={{ color: 'error.main', bgcolor: 'rgba(239, 68, 68, 0.1)' }}>
                        <LogOut size={20} />
                    </IconButton>
                </Tooltip>
            </Box>
        </Box>
    );
}

