'use client';

import { Box, List, ListItemButton, ListItemIcon, ListItemText, Typography, Avatar, Divider } from '@mui/material';
import {
    LayoutDashboard, Users, FileText, CreditCard,
    BarChart3, Settings, Shield, Activity, ChevronRight, FolderKanban, User, MessageSquare
} from 'lucide-react';
import { useState, useEffect } from 'react';

export const SIDEBAR_ITEMS = [
    { label: 'Dashboard', icon: LayoutDashboard, id: 'dashboard' },
    { label: 'Profile', icon: User, id: 'profile' },
    { label: 'Users', icon: Users, id: 'users' },
    { label: 'Content / Data', icon: FileText, id: 'content' },
    { label: 'Client Msgs', icon: MessageSquare, id: 'messages' }, // Added Messages
    { label: 'User Panel Controls', icon: FolderKanban, id: 'user-panel' },
    { label: 'Transactions', icon: CreditCard, id: 'transactions' },
    { label: 'Analytics / Reports', icon: BarChart3, id: 'analytics' },
    { label: 'System / Settings', icon: Settings, id: 'settings' },
    { label: 'Roles & Permissions', icon: Shield, id: 'roles' },
    { label: 'Logs & Support', icon: Activity, id: 'logs' },
];

interface AdminSidebarProps {
    currentSection: string;
    setCurrentSection: (id: string) => void;
}

export default function AdminSidebar({ currentSection, setCurrentSection }: AdminSidebarProps) {
    const [userName, setUserName] = useState('Admin User');
    const [userEmail, setUserEmail] = useState('admin@sdevx.com');
    const [userInitials, setUserInitials] = useState('AD');
    const [userImage, setUserImage] = useState('');

    useEffect(() => {
        // Get user data from localStorage
        const userData = localStorage.getItem('user');
        if (userData) {
            try {
                const user = JSON.parse(userData);
                setUserName(user.name || 'Admin User');
                setUserEmail(user.email || 'admin@sdevx.com');
                setUserImage(user.image || '');

                // Generate initials from name
                const nameParts = (user.name || 'Admin User').split(' ');
                const initials = nameParts.length > 1
                    ? nameParts[0].charAt(0) + nameParts[1].charAt(0)
                    : nameParts[0].charAt(0) + (nameParts[0].charAt(1) || '');
                setUserInitials(initials.toUpperCase());
            } catch (error) {
                console.error('Error parsing user data:', error);
            }
        }
    }, []);

    return (
        <Box sx={{
            width: 280,
            flexShrink: 0,
            bgcolor: 'var(--card-bg)', // Using CSS variable
            borderRight: '1px solid var(--card-border)',
            display: 'flex',
            flexDirection: 'column',
            height: '100vh',
            backdropFilter: 'blur(10px)',
            position: 'sticky',
            top: 0,
            zIndex: 10
        }}>
            <Box p={3} display="flex" alignItems="center" gap={2}>
                <Avatar sx={{
                    bgcolor: 'var(--primary-color)',
                    width: 40,
                    height: 40,
                    boxShadow: '0 0 15px var(--accent-glow)'
                }}>S</Avatar>
                <Box>
                    <Typography variant="h6" fontWeight="bold" color="text.primary" sx={{ lineHeight: 1.2 }}>
                        SDEVX
                    </Typography>
                    <Typography variant="caption" color="text.secondary">
                        Admin Console
                    </Typography>
                </Box>
            </Box>

            <Divider sx={{ mb: 2, opacity: 0.5 }} />

            <List component="nav" sx={{ px: 2, flex: 1, overflowY: 'auto' }}>
                <Typography variant="subtitle2" color="text.secondary" sx={{ px: 2, mb: 1, fontSize: '0.75rem', textTransform: 'uppercase', letterSpacing: '0.5px' }}>
                    Main Menu
                </Typography>
                {SIDEBAR_ITEMS.map((item) => {
                    const Icon = item.icon;
                    const isActive = currentSection === item.id;
                    return (
                        <ListItemButton
                            key={item.id}
                            selected={isActive}
                            onClick={() => setCurrentSection(item.id)}
                            sx={{
                                borderRadius: '12px',
                                mb: 0.5,
                                transition: 'all 0.2s',
                                bgcolor: isActive ? 'rgba(99, 102, 241, 0.08) !important' : 'transparent',
                                color: isActive ? 'var(--primary-color)' : 'text.secondary',
                                '&:hover': {
                                    bgcolor: 'rgba(99, 102, 241, 0.04)',
                                    color: 'var(--primary-color)',
                                    transform: 'translateX(4px)',
                                    '& .MuiListItemIcon-root': { color: 'var(--primary-color)' }
                                },
                            }}
                        >
                            <ListItemIcon sx={{
                                minWidth: 40,
                                color: isActive ? 'var(--primary-color)' : 'text.secondary',
                            }}>
                                <Icon size={20} />
                            </ListItemIcon>
                            <ListItemText
                                primary={item.label}
                                primaryTypographyProps={{
                                    fontWeight: isActive ? 600 : 500,
                                    fontSize: '0.9rem'
                                }}
                            />
                            {isActive && <ChevronRight size={16} />}
                        </ListItemButton>
                    );
                })}
            </List>

            <Box p={3}>
                <Box p={2} borderRadius={3} sx={{
                    background: 'linear-gradient(135deg, rgba(99, 102, 241, 0.1) 0%, rgba(217, 70, 239, 0.1) 100%)',
                    border: '1px solid rgba(99, 102, 241, 0.1)'
                }}>
                    <Box display="flex" alignItems="center" gap={1.5}>
                        <Avatar
                            src={userImage}
                            sx={{ width: 32, height: 32, fontSize: '0.8rem', bgcolor: 'transparent', color: 'var(--primary-color)', border: '1px solid var(--primary-color)', flexShrink: 0 }}
                        >
                            {!userImage && userInitials}
                        </Avatar>
                        <Box sx={{ minWidth: 0, flex: 1 }}>
                            <Typography variant="body2" fontWeight="600" color="text.primary" sx={{
                                overflow: 'hidden',
                                textOverflow: 'ellipsis',
                                whiteSpace: 'nowrap'
                            }}>
                                {userName}
                            </Typography>
                            <Typography variant="caption" color="text.secondary" sx={{
                                display: 'block',
                                overflow: 'hidden',
                                textOverflow: 'ellipsis',
                                whiteSpace: 'nowrap'
                            }}>
                                {userEmail}
                            </Typography>
                        </Box>
                    </Box>
                </Box>
            </Box>
        </Box>
    );
}

'use client';

import { Box, Grid, TextField, Divider } from '@mui/material';

interface AboutSectionProps {
    data: any;
    handleChange: (field: string, value: any) => void;
}

export default function AboutSection({ data, handleChange }: AboutSectionProps) {
    return (
        <Box>
            <Box mb={4} sx={{ display: 'grid', gridTemplateColumns: 'minmax(0, 1fr)', gap: 3 }}>
                <Box>
                    <TextField
                        fullWidth
                        label="Section Heading"
                        value={data.heading}
                        onChange={(e) => handleChange('heading', e.target.value)}
                        sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px', bgcolor: 'white' } }}
                    />
                </Box>
                <Box>
                    <TextField
                        fullWidth
                        label="Sub Heading"
                        value={data.subHeading}
                        onChange={(e) => handleChange('subHeading', e.target.value)}
                        sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px', bgcolor: 'white' } }}
                    />
                </Box>
            </Box>

            <Divider sx={{ mb: 3 }} textAlign="left">Paragraphs</Divider>

            {data.paragraphs.map((para: string, index: number) => (
                <TextField
                    key={index}
                    fullWidth
                    multiline
                    rows={3}
                    label={`Paragraph ${index + 1}`}
                    sx={{ mb: 3, '& .MuiOutlinedInput-root': { borderRadius: '12px', bgcolor: 'white' } }}
                    value={para}
                    onChange={(e) => {
                        const newParas = [...data.paragraphs];
                        newParas[index] = e.target.value;
                        handleChange('paragraphs', newParas);
                    }}
                />
            ))}
        </Box>
    );
}

import { useState, useEffect } from 'react';
import {
    Box, Paper, Typography, Stack, Grid, Card, CardContent, Button, Divider, LinearProgress, CircularProgress
} from '@mui/material';
import {
    Download, FileText, FileSpreadsheet, TrendingUp, Users, Activity, ExternalLink
} from 'lucide-react';

export default function AnalyticsSection() {
    const [exporting, setExporting] = useState(false);
    const [data, setData] = useState<any>(null);
    const [loading, setLoading] = useState(true);

    const fetchAnalytics = async () => {
        try {
            const res = await fetch('/api/analytics', { credentials: 'include' });
            if (res.ok) {
                const fetchedData = await res.json();
                setData(fetchedData);
            }
        } catch (error) {
            console.error('Error fetching analytics:', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchAnalytics();
    }, []);

    const handleExport = (type: string) => {
        setExporting(true);
        // Simulate export delay
        setTimeout(() => {
            alert(`Exporting ${type} report...`);
            setExporting(false);
        }, 1000);
    };

    // calculate total feature usage from project breakdown (example mapping)
    const getProjectBreakdown = () => {
        if (!data?.projects?.breakdown) return [];
        const total = data.projects.total || 1;
        return data.projects.breakdown.map((b: any) => ({
            label: b._id,
            value: Math.round((b.count / total) * 100),
            count: b.count,
            color: b._id === 'Completed' ? '#10b981' : b._id === 'In Progress' ? '#3b82f6' : '#f59e0b'
        }));
    };

    if (loading) return <Box display="flex" justifyContent="center" p={4}><CircularProgress /></Box>;

    const projectStats = getProjectBreakdown();

    return (
        <Stack spacing={4}>
            {/* Header & Export */}
            <Paper elevation={0} sx={{ p: 2, borderRadius: '16px', border: '1px solid #e2e8f0' }}>
                <Box display="flex" justifyContent="space-between" alignItems="center" flexWrap="wrap" gap={2}>
                    <Box>
                        <Typography variant="h6" fontWeight="bold">Analytics Overview</Typography>
                        <Typography variant="body2" color="text.secondary">Real-time usage and revenue reports</Typography>
                    </Box>
                    <Stack direction="row" spacing={1}>
                        <Button
                            variant="outlined"
                            startIcon={<FileText size={16} />}
                            onClick={() => handleExport('PDF')}
                            disabled={exporting}
                            sx={{ color: 'text.secondary', borderColor: '#e2e8f0', textTransform: 'none' }}
                        >
                            PDF
                        </Button>
                        <Button
                            variant="outlined"
                            startIcon={<FileSpreadsheet size={16} />}
                            onClick={() => handleExport('CSV')}
                            disabled={exporting}
                            sx={{ color: 'text.secondary', borderColor: '#e2e8f0', textTransform: 'none' }}
                        >
                            Excel
                        </Button>
                    </Stack>
                </Box>
            </Paper>

            {/* Growth & Revenue Components */}
            <Box display="grid" gridTemplateColumns={{ xs: '1fr', md: '2fr 1fr' }} gap={3}>
                {/* User Growth (Line Chart Simulation) */}
                <Box>
                    <Paper elevation={0} sx={{ p: 3, borderRadius: '16px', border: '1px solid #e2e8f0', height: '100%' }}>
                        <Box display="flex" justifyContent="space-between" alignItems="center" mb={4}>
                            <Box display="flex" alignItems="center" gap={1}>
                                <Box p={1} bgcolor="#eff6ff" borderRadius="8px">
                                    <TrendingUp size={20} color="#3b82f6" />
                                </Box>
                                <Typography variant="subtitle1" fontWeight={600}>User Growth</Typography>
                            </Box>
                            <Box display="flex" gap={2}>
                                <Box display="flex" alignItems="center" gap={1}>
                                    <Box width={8} height={8} borderRadius="50%" bgcolor="#3b82f6" />
                                    <Typography variant="caption">New Users ({data?.users?.total || 0})</Typography>
                                </Box>
                            </Box>
                        </Box>

                        {/* Custom CSS Chart Placeholder - using real growth data if available, else placeholder */}
                        <Box height={300} display="flex" alignItems="flex-end" justifyContent="space-between" gap={1} sx={{ opacity: 0.9 }}>
                            {/* Map growth data or fallback to 0. Ideally verify buckets match month names. */}
                            {['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'].map((month, i) => {
                                // Find bucket for this month (simplified logic)
                                const count = data?.users?.growth?.find((g: any) => g._id === i + 1)?.count || 0;
                                // Scale height: assume max 10 users for visual scaling for now
                                const height = Math.min((count / 10) * 200 + 20, 250);
                                return (
                                    <Box key={i} width="100%" display="flex" flexDirection="column" gap={0.5}>
                                        <Box
                                            sx={{
                                                height: `${height}px`,
                                                bgcolor: i === 5 ? '#3b82f6' : '#bfdbfe',
