import { useState, useEffect } from 'react';
import {
    Box, Paper, Typography, Stack, Tabs, Tab, Chip, TextField, InputAdornment,
    List, ListItem, ListItemText, ListItemAvatar, Avatar, Divider, CircularProgress,
    Button
} from '@mui/material';
import {
    Search, Filter, Info, AlertTriangle, AlertCircle, Clock, User, Server, Database,
    RefreshCcw, CheckCircle
} from 'lucide-react';

interface Log {
    _id: string;
    action: string;
    details: string;
    type: 'info' | 'warning' | 'error' | 'success';
    user: string;
    source: string;
    createdAt: string;
}

export default function LogsSection() {
    const [tabValue, setTabValue] = useState(0); // 0: All, 1: Activity (User), 2: System
    const [searchTerm, setSearchTerm] = useState('');
    const [filter, setFilter] = useState('all');
    const [logs, setLogs] = useState<Log[]>([]);
    const [loading, setLoading] = useState(true);

    const fetchLogs = async () => {
        setLoading(true);
        try {
            const params = new URLSearchParams();
            if (searchTerm) params.append('search', searchTerm);
            if (filter !== 'all') params.append('type', filter);

            const res = await fetch(`/api/logs?${params.toString()}`, {
                credentials: 'include'
            });

            if (res.ok) {
                const data = await res.json();
                setLogs(data.logs || []);
            }
        } catch (error) {
            console.error('Error fetching logs:', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        const timer = setTimeout(() => {
            fetchLogs();
        }, 500); // Debounce search
        return () => clearTimeout(timer);
    }, [searchTerm, filter]);

    const handleTabChange = (_event: React.SyntheticEvent, newValue: number) => {
        setTabValue(newValue);
        // Logic to filter by user vs system if needed, or just visual
    };

    const getIcon = (type: string) => {
        switch (type) {
            case 'error': return <AlertCircle size={20} color="#ef4444" />;
            case 'warning': return <AlertTriangle size={20} color="#f59e0b" />;
            case 'success': return <CheckCircle size={20} color="#10b981" />;
            default: return <Info size={20} color="#3b82f6" />;
        }
    };

    // Filter logs based on tabs (client-side for now, or could be API param)
    const displayLogs = logs.filter(log => {
        if (tabValue === 0) return true;
        // Simple heuristic: User actions vs System actions
        const isSystem = log.user === 'System' || log.source === 'System' || log.source === 'Database';
        if (tabValue === 1) return !isSystem; // Activity Logs
        if (tabValue === 2) return isSystem;  // System Logs
        return true;
    });

    return (
        <Stack spacing={3}>
            {/* Header & Controls */}
            <Paper elevation={0} sx={{ p: 2, borderRadius: '16px', border: '1px solid #e2e8f0' }}>
                <Box display="flex" justifyContent="space-between" alignItems="center" flexWrap="wrap" gap={2}>
                    <Box>
                        <Tabs
                            value={tabValue}
                            onChange={handleTabChange}
                            sx={{ '& .MuiTab-root': { textTransform: 'none', minHeight: 48 } }}
                        >
                            <Tab label="All Logs" />
                            <Tab label="Activity Logs" icon={<User size={16} />} iconPosition="start" />
                            <Tab label="System Logs" icon={<Server size={16} />} iconPosition="start" />
                        </Tabs>
                    </Box>
                    <Box display="flex" gap={2} alignItems="center">
                        <Stack direction="row" spacing={1}>
                            <Button
                                size="small"
                                startIcon={<RefreshCcw size={14} />}
                                onClick={fetchLogs}
                                sx={{ minWidth: 'auto', px: 1 }}
                            />
                            <Chip
                                label="All"
                                onClick={() => setFilter('all')}
                                color={filter === 'all' ? 'primary' : 'default'}
                                variant={filter === 'all' ? 'filled' : 'outlined'}
                                size="small"
                            />
                            <Chip
                                label="Errors"
                                onClick={() => setFilter('error')}
                                color={filter === 'error' ? 'error' : 'default'}
                                variant={filter === 'error' ? 'filled' : 'outlined'}
                                size="small"
                            />
                        </Stack>
                        <TextField
                            size="small"
                            placeholder="Search logs..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position="start">
                                        <Search size={16} color="#94a3b8" />
                                    </InputAdornment>
                                ),
                            }}
                            sx={{ width: 200 }}
                        />
                    </Box>
                </Box>
            </Paper>

            {/* Logs List */}
            <Paper elevation={0} sx={{ borderRadius: '16px', border: '1px solid #e2e8f0', minHeight: 400 }}>
                {loading ? (
                    <Box display="flex" justifyContent="center" alignItems="center" height={400}>
                        <CircularProgress />
                    </Box>
                ) : displayLogs.length > 0 ? (
                    <List sx={{ p: 0 }}>
                        {displayLogs.map((log, index) => (
                            <Box key={log._id}>
                                <ListItem alignItems="flex-start" sx={{ p: 3, '&:hover': { bgcolor: '#f8fafc' } }}>
                                    <ListItemAvatar>
                                        <Avatar sx={{ bgcolor: log.type === 'error' ? '#fef2f2' : log.type === 'warning' ? '#fffbeb' : log.type === 'success' ? '#ecfdf5' : '#eff6ff' }}>
                                            {getIcon(log.type)}
                                        </Avatar>
                                    </ListItemAvatar>
                                    <ListItemText
                                        primary={
                                            <Box display="flex" justifyContent="space-between" alignItems="center">
                                                <Typography variant="subtitle2" fontWeight={600}>
                                                    {log.action}
                                                </Typography>
                                                <Typography variant="caption" color="text.secondary" display="flex" alignItems="center" gap={0.5}>
                                                    <Clock size={12} /> {new Date(log.createdAt).toLocaleString()}
                                                </Typography>
                                            </Box>
                                        }
                                        secondary={
                                            <Box mt={0.5}>
                                                <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                                                    {log.details}
                                                </Typography>
                                                <Stack direction="row" spacing={1}>
                                                    <Chip
                                                        label={log.user || log.source}
                                                        size="small"
                                                        sx={{ borderRadius: '4px', height: 20, fontSize: '10px' }}
                                                        icon={tabValue === 1 ? <User size={10} /> : <Database size={10} />}
                                                    />
                                                    <Chip
                                                        label={log.type.toUpperCase()}
                                                        size="small"
                                                        color={log.type === 'error' ? 'error' : log.type === 'warning' ? 'warning' : log.type === 'success' ? 'success' : 'primary'}
                                                        variant="outlined"
                                                        sx={{ borderRadius: '4px', height: 20, fontSize: '10px' }}
                                                    />
                                                </Stack>
                                            </Box>
                                        }
                                    />
                                </ListItem>
                                {index < displayLogs.length - 1 && <Divider variant="inset" component="li" />}
                            </Box>
                        ))}
                    </List>
                ) : (
                    <Box display="flex" flexDirection="column" alignItems="center" justifyContent="center" height={400} color="text.secondary">
                        <Search size={48} style={{ opacity: 0.2, marginBottom: 16 }} />
                        <Typography>No logs found matching your criteria</Typography>
                    </Box>
                )}
            </Paper>
        </Stack>
    );
}

import { useState, useEffect } from 'react';
import { Box, Paper, Typography, Stack, Avatar, Chip, CircularProgress, Alert, Button, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Snackbar } from '@mui/material';
import { Search, Mail, CheckCircle, Clock } from 'lucide-react';

interface Message {
    _id: string;
    fromUserId: {
        _id: string;
        name: string;
        email: string;
        image: string;
    };
    subject: string;
    message: string;
    isRead: boolean;
    createdAt: string;
}

export default function AdminMessagesSection() {
    const [messages, setMessages] = useState<Message[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const [openReplyDialog, setOpenReplyDialog] = useState(false);
    const [replyTo, setReplyTo] = useState<Message | null>(null);
    const [replySubject, setReplySubject] = useState('');
    const [replyMessage, setReplyMessage] = useState('');
    const [sending, setSending] = useState(false);
    const [toast, setToast] = useState({ open: false, message: '', severity: 'success' as 'success' | 'error' });

    useEffect(() => {
        fetchMessages();
    }, []);

    const fetchMessages = async () => {
        try {
            setLoading(true);
            const res = await fetch('/api/admin/messages');
            if (!res.ok) throw new Error('Failed to fetch messages');
            const data = await res.json();
            setMessages(data.messages || []);
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleOpenReply = (msg: Message) => {
        setReplyTo(msg);
        setReplySubject(`Re: ${msg.subject}`);
        setOpenReplyDialog(true);
    };

    const handleSendReply = async () => {
        if (!replySubject || !replyMessage || !replyTo) return;
        setSending(true);

        try {
            const res = await fetch('/api/user/messages', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    toUserId: replyTo.fromUserId._id,
                    subject: replySubject,
                    message: replyMessage
                })
            });

            if (!res.ok) {
                const data = await res.json();
                throw new Error(data.error || 'Failed to send reply');
            }

            setToast({ open: true, message: 'Reply sent successfully', severity: 'success' });
            setOpenReplyDialog(false);
            setReplyMessage('');
            // Optionally refresh messages if you want to show the sent reply in a thread view
        } catch (err: any) {
            setToast({ open: true, message: err.message, severity: 'error' });
        } finally {
            setSending(false);
        }
    };

    const formatTime = (dateString: string) => {
        return new Date(dateString).toLocaleString();
    };

    return (
        <Stack spacing={3}>
            <Box>
                <Typography variant="h5" fontWeight="bold">
                    Client Messages
                </Typography>
                <Typography variant="body2" color="text.secondary">
                    View inquiries and support requests from users
                </Typography>
            </Box>

            {loading && <Box display="flex" justifyContent="center" p={4}><CircularProgress /></Box>}
            {error && <Alert severity="error">{error}</Alert>}

            {!loading && !error && messages.length === 0 && (
                <Paper elevation={0} sx={{ p: 4, borderRadius: '16px', border: '1px solid #e2e8f0', textAlign: 'center' }}>
                    <Mail size={48} color="#94a3b8" style={{ marginBottom: 16 }} />
                    <Typography color="text.secondary">No messages found.</Typography>
                </Paper>
            )}

            {!loading && !error && (
                <Stack spacing={2}>
                    {messages.map((msg) => (
                        <Paper
                            key={msg._id}
                            elevation={0}
                            sx={{
                                p: 3,
                                borderRadius: '16px',
                                border: '1px solid #e2e8f0',
                                transition: 'all 0.2s',
                                '&:hover': { boxShadow: '0 4px 12px rgba(0,0,0,0.05)' }
                            }}
                        >
                            <Box display="flex" gap={2} alignItems="flex-start">
                                <Avatar
                                    src={msg.fromUserId?.image}
                                    sx={{ width: 48, height: 48, bgcolor: 'primary.main' }}
                                >
                                    {msg.fromUserId?.name?.charAt(0) || 'U'}
                                </Avatar>
                                <Box flex={1}>
                                    <Box display="flex" justifyContent="space-between" alignItems="center" mb={1}>
                                        <Box>
                                            <Typography variant="subtitle1" fontWeight="bold">
                                                {msg.fromUserId?.name || 'Unknown User'}
                                            </Typography>
                                            <Typography variant="caption" color="text.secondary">
                                                {msg.fromUserId?.email}
                                            </Typography>
                                        </Box>
                                        <Stack direction="row" spacing={1} alignItems="center">
                                            <Chip
                                                label={msg.isRead ? 'Read' : 'New'}
                                                size="small"
                                                color={msg.isRead ? 'default' : 'error'}
                                                variant={msg.isRead ? 'outlined' : 'filled'}
                                            />
                                            <Typography variant="caption" color="text.secondary">
                                                {formatTime(msg.createdAt)}
                                            </Typography>
                                        </Stack>
                                    </Box>

                                    <Box bgcolor="#f8fafc" p={2} borderRadius="12px" border="1px solid #e2e8f0">
                                        <Typography variant="subtitle2" fontWeight="600" gutterBottom>
                                            {msg.subject}
                                        </Typography>
                                        <Typography variant="body2" color="text.secondary">
                                            {msg.message}
                                        </Typography>
                                    </Box>

                                    <Box mt={2} display="flex" justifyContent="flex-end" gap={1}>
                                        <Button
                                            variant="contained"
                                            size="small"
                                            sx={{ borderRadius: '8px', textTransform: 'none', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}
                                            onClick={() => handleOpenReply(msg)}
                                        >
                                            Reply
                                        </Button>
                                    </Box>
                                </Box>
                            </Box>
                        </Paper>
                    ))}
                </Stack>
            )}
            {/* Reply Dialog */}
            <Dialog
                open={openReplyDialog}
                onClose={() => setOpenReplyDialog(false)}
                PaperProps={{ sx: { borderRadius: '16px', maxWidth: 500, width: '100%' } }}
            >
                <DialogTitle fontWeight="bold">
                    Reply to {replyTo?.fromUserId?.name}
                </DialogTitle>
                <DialogContent>
                    <Stack spacing={2} mt={1}>
                        <TextField
                            label="Subject"
                            fullWidth
                            value={replySubject}
                            onChange={(e) => setReplySubject(e.target.value)}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                        <TextField
                            label="Message"
                            fullWidth
                            multiline
                            rows={4}
                            value={replyMessage}
                            onChange={(e) => setReplyMessage(e.target.value)}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Stack>
                </DialogContent>
                <DialogActions sx={{ p: 3 }}>
                    <Button onClick={() => setOpenReplyDialog(false)} sx={{ borderRadius: '8px' }}>Cancel</Button>
                    <Button
                        variant="contained"
                        onClick={handleSendReply}
                        disabled={sending}
                        sx={{ borderRadius: '8px', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}
                    >
                        {sending ? 'Sending...' : 'Send Reply'}
                    </Button>
                </DialogActions>
            </Dialog>

            {/* Toast Notification */}
            <Snackbar
                open={toast.open}
                autoHideDuration={4000}
                onClose={() => setToast({ ...toast, open: false })}
                anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
            >
                <Alert
                    onClose={() => setToast({ ...toast, open: false })}
                    severity={toast.severity}
                    sx={{ borderRadius: '12px' }}
                >
                    {toast.message}
                </Alert>
            </Snackbar>
        </Stack>
    );
}

'use client';

import { Box, Paper, Typography, Stack, TextField, Button, Avatar, Grid, Alert, Snackbar } from '@mui/material';
import { User, Mail, Phone, MapPin, Save, Shield } from 'lucide-react';
import { useState, useEffect } from 'react';

interface ProfileData {
    name: string;
    email: string;
    phone: string;
    department: string;
    role: string;
    image: string;
}

export default function ProfileSection() {
    const [editing, setEditing] = useState(false);
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [profileData, setProfileData] = useState<ProfileData>({
        name: '',
        email: '',
        phone: '',
        department: '',
        role: '',
        image: ''
    });

    // Password change state
    const [passwordData, setPasswordData] = useState({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
    });
    const [changingPassword, setChangingPassword] = useState(false);

    // Toast notifications
    const [toast, setToast] = useState({ open: false, message: '', severity: 'success' as 'success' | 'error' });

    // Fetch profile data on mount
    useEffect(() => {
        fetchProfile();
    }, []);

    const fetchProfile = async () => {
        try {
            const res = await fetch('/api/profile', {
                credentials: 'include'
            });

            if (!res.ok) {
                throw new Error('Failed to fetch profile');
            }

            const data = await res.json();
            setProfileData({
                name: data.user.name || '',
                email: data.user.email || '',
                phone: data.user.phone || '',
                department: data.user.department || '',
                role: data.user.role || '',
                image: data.user.image || ''
            });
        } catch (error: any) {
            console.error('Profile fetch error:', error);
            setToast({ open: true, message: 'Failed to load profile', severity: 'error' });
        } finally {
            setLoading(false);
        }
    };

    const handleSaveProfile = async () => {
        if (!profileData.name.trim()) {
            setToast({ open: true, message: 'Name is required', severity: 'error' });
            return;
        }

        setSaving(true);
        try {
            const res = await fetch('/api/profile', {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                credentials: 'include',
                body: JSON.stringify({
                    name: profileData.name,
                    phone: profileData.phone,
                    department: profileData.department,
                    image: profileData.image
                })
            });

            const data = await res.json();

            if (!res.ok) {
                throw new Error(data.error || 'Failed to update profile');
            }

            setToast({ open: true, message: 'Profile updated successfully!', severity: 'success' });
            setEditing(false);

            // Update localStorage user data
            const storedUser = localStorage.getItem('user');
            if (storedUser) {
                const user = JSON.parse(storedUser);
                user.name = data.user.name;
                user.image = data.user.image;
                localStorage.setItem('user', JSON.stringify(user));
                // Dispatch event to update sidebar
                window.dispatchEvent(new Event('user-updated'));
            }
        } catch (error: any) {
            console.error('Profile update error:', error);
            setToast({ open: true, message: error.message || 'Failed to update profile', severity: 'error' });
        } finally {
            setSaving(false);
        }
    };

    const handleChangePassword = async () => {
        // Validation
        if (!passwordData.currentPassword || !passwordData.newPassword || !passwordData.confirmPassword) {
            setToast({ open: true, message: 'All password fields are required', severity: 'error' });
            return;
        }

        if (passwordData.newPassword.length < 6) {
            setToast({ open: true, message: 'New password must be at least 6 characters', severity: 'error' });
            return;
        }

        if (passwordData.newPassword !== passwordData.confirmPassword) {
            setToast({ open: true, message: 'New passwords do not match', severity: 'error' });
            return;
        }

        setChangingPassword(true);
        try {
            const res = await fetch('/api/profile/password', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                credentials: 'include',
                body: JSON.stringify({
                    currentPassword: passwordData.currentPassword,
                    newPassword: passwordData.newPassword
                })
            });

            const data = await res.json();

            if (!res.ok) {
                throw new Error(data.error || 'Failed to change password');
            }

            setToast({ open: true, message: 'Password changed successfully!', severity: 'success' });
            setPasswordData({ currentPassword: '', newPassword: '', confirmPassword: '' });
        } catch (error: any) {
            console.error('Password change error:', error);
            setToast({ open: true, message: error.message || 'Failed to change password', severity: 'error' });
        } finally {
            setChangingPassword(false);
        }
    };

    const handleFileUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (!file) return;

        // Create FormData
        const formData = new FormData();
        formData.append('file', file);

        try {
            setToast({ open: true, message: 'Uploading photo...', severity: 'success' }); // Info level would be better, but re-using success

            const res = await fetch('/api/upload', {
                method: 'POST',
                body: formData,
                credentials: 'include'
            });

            const data = await res.json();

            if (!res.ok) {
                throw new Error(data.error || 'Upload failed');
            }

            // Update profile data with new image URL
            setProfileData(prev => ({ ...prev, image: data.imageUrl }));

            setToast({ open: true, message: 'Photo uploaded! Click Save to apply.', severity: 'success' });
        } catch (error: any) {
            console.error('Upload error:', error);
            setToast({ open: true, message: error.message || 'Failed to upload photo', severity: 'error' });
        }
    };

    if (loading) {
        return (
            <Box display="flex" alignItems="center" justifyContent="center" minHeight="400px">
                <Typography>Loading profile...</Typography>
            </Box>
        );
    }

    return (
        <Stack spacing={3}>
            <Box display="flex" justifyContent="space-between" alignItems="center">
                <Box>
                    <Typography variant="h5" fontWeight="bold">
                        Admin Profile Settings
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        Manage your administrator account information
                    </Typography>
                </Box>
                <Button
                    variant={editing ? 'contained' : 'outlined'}
                    startIcon={editing ? <Save size={18} /> : <User size={18} />}
                    onClick={editing ? handleSaveProfile : () => setEditing(true)}
                    disabled={saving}
                    sx={{
                        textTransform: 'none',
                        borderRadius: '12px',
                        ...(editing && {
                            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                        }),
                    }}
                >
                    {saving ? 'Saving...' : editing ? 'Save Changes' : 'Edit Profile'}
                </Button>
            </Box>

            <Paper elevation={0} sx={{ p: 3, borderRadius: '16px', border: '1px solid #e2e8f0' }}>
                <Box display="flex" alignItems="center" gap={3} mb={4}>
                    <Avatar
                        src={profileData.image}
                        sx={{
                            width: 80,
                            height: 80,
                            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                            fontSize: 32
                        }}
                    >
                        {!profileData.image && profileData.name.charAt(0).toUpperCase()}
                    </Avatar>
                    <Box>
                        <Box display="flex" alignItems="center" gap={1} mb={0.5}>
                            <Typography variant="h6" fontWeight="bold">
                                Profile Picture
                            </Typography>
                            <Shield size={16} color="#667eea" />
                        </Box>
                        <Typography variant="body2" color="text.secondary" mb={1}>
                            JPG, PNG or GIF. Max size 2MB
                        </Typography>
                        {editing && (
                            <>
                                <input
                                    accept="image/*"
                                    style={{ display: 'none' }}
                                    id="raised-button-file"
                                    type="file"
                                    onChange={handleFileUpload}
                                />
                                <label htmlFor="raised-button-file">
                                    <Button variant="outlined" component="span" size="small" sx={{ textTransform: 'none', borderRadius: '8px' }}>
                                        Upload Photo
                                    </Button>
                                </label>
                            </>
                        )}
                    </Box>
                </Box>

                <Grid container spacing={3}>
                    <Grid item xs={12} sm={6}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Full Name *
                        </Typography>
                        <TextField
                            fullWidth
                            value={profileData.name}
                            onChange={(e) => setProfileData({ ...profileData, name: e.target.value })}
                            disabled={!editing}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>

                    <Grid item xs={12} sm={6}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Email Address
                        </Typography>
                        <TextField
                            fullWidth
                            value={profileData.email}
                            disabled
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>

                    <Grid item xs={12} sm={6}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Phone Number
                        </Typography>
                        <TextField
                            fullWidth
                            value={profileData.phone}
                            onChange={(e) => setProfileData({ ...profileData, phone: e.target.value })}
                            disabled={!editing}
                            placeholder="+1 (555) 000-0000"
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>

                    <Grid item xs={12} sm={6}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Role
                        </Typography>
                        <TextField
                            fullWidth
                            value={profileData.role === 'admin' ? 'Administrator' : 'User'}
                            disabled
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>

                    <Grid item xs={12}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Department
                        </Typography>
                        <TextField
                            fullWidth
                            value={profileData.department}
                            onChange={(e) => setProfileData({ ...profileData, department: e.target.value })}
                            disabled={!editing}
                            placeholder="System Administration"
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>
                </Grid>
            </Paper>

            <Paper elevation={0} sx={{ p: 3, borderRadius: '16px', border: '1px solid #e2e8f0' }}>
                <Typography variant="h6" fontWeight="bold" mb={2}>
                    Change Password
                </Typography>
                <Grid container spacing={2}>
                    <Grid item xs={12}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Current Password
                        </Typography>
                        <TextField
                            fullWidth
                            type="password"
                            value={passwordData.currentPassword}
                            onChange={(e) => setPasswordData({ ...passwordData, currentPassword: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            New Password
                        </Typography>
                        <TextField
                            fullWidth
                            type="password"
                            value={passwordData.newPassword}
                            onChange={(e) => setPasswordData({ ...passwordData, newPassword: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Confirm Password
                        </Typography>
                        <TextField
                            fullWidth
                            type="password"
                            value={passwordData.confirmPassword}
                            onChange={(e) => setPasswordData({ ...passwordData, confirmPassword: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <Button
                            variant="outlined"
                            onClick={handleChangePassword}
                            disabled={changingPassword}
                            sx={{ textTransform: 'none', borderRadius: '12px' }}
                        >
                            {changingPassword ? 'Updating...' : 'Update Password'}
                        </Button>
                    </Grid>
                </Grid>
            </Paper>

            <Paper elevation={0} sx={{ p: 3, borderRadius: '16px', border: '1px solid #e2e8f0', bgcolor: 'rgba(99, 102, 241, 0.05)' }}>
                <Box display="flex" alignItems="start" gap={2}>
                    <Shield size={20} color="#667eea" />
                    <Box>
                        <Typography variant="subtitle2" fontWeight="bold" color="primary" mb={0.5}>
                            Administrator Privileges
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                            You have full system access and administrative privileges. Use your permissions responsibly.
                        </Typography>
                    </Box>
                </Box>
            </Paper>

            {/* Toast Notification */}
            <Snackbar
                open={toast.open}
                autoHideDuration={4000}
                onClose={() => setToast({ ...toast, open: false })}
                anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
            >
                <Alert
                    onClose={() => setToast({ ...toast, open: false })}
                    severity={toast.severity}
                    sx={{ borderRadius: '12px' }}
                >
                    {toast.message}
                </Alert>
            </Snackbar>
        </Stack>
    );
}

import { useState, useEffect } from 'react';
import {
    Box, Paper, Typography, Stack, Button, Table, TableBody, TableCell, TableContainer,
    TableHead, TableRow, IconButton, Chip, Dialog, DialogTitle, DialogContent, DialogActions,
    TextField, Grid, MenuItem, CircularProgress, Snackbar, Alert
} from '@mui/material';
import { Plus, Edit, Trash2, Eye } from 'lucide-react';

interface Project {
    _id: string;
    name: string;
    userId: any; // Populated user object or ID
    clientName?: string; // Derived for display
    status: 'Planning' | 'In Progress' | 'Review' | 'Completed';
    progress: number;
    dueDate: string;
    team: string;
    description: string;
}

interface User {
    _id: string;
    name: string;
    email: string;
}

export default function ProjectsManagementSection() {
    const [projects, setProjects] = useState<Project[]>([]);
    const [users, setUsers] = useState<User[]>([]);
    const [loading, setLoading] = useState(true);
    const [openDialog, setOpenDialog] = useState(false);
    const [editingProject, setEditingProject] = useState<Project | null>(null);
    const [actionLoading, setActionLoading] = useState(false);
    const [toast, setToast] = useState({ open: false, message: '', severity: 'success' as 'success' | 'error' });

    // Form State
    const [formData, setFormData] = useState({
        name: '',
        userId: '',
        status: 'Planning',
        progress: 0,
        dueDate: '',
        team: '',
        description: ''
    });

    const fetchProjects = async () => {
        try {
            const res = await fetch('/api/projects', { credentials: 'include' });
            if (res.ok) {
                const data = await res.json();
                setProjects(data.projects || []);
            } else {
                console.error('Failed to fetch projects', await res.text());
                setToast({ open: true, message: 'Failed to fetch projects', severity: 'error' });
            }
        } catch (error) {
            console.error('Error fetching projects:', error);
            setToast({ open: true, message: 'Error fetching projects', severity: 'error' });
        } finally {
            setLoading(false);
        }
    };

    const fetchUsers = async () => {
        try {
            const res = await fetch('/api/users', { credentials: 'include' });
            if (res.ok) {
                const data = await res.json();
                setUsers(data.users || []);
            } else {
                console.error('Failed to fetch users', await res.text());
                setToast({ open: true, message: 'Failed to fetch clients list', severity: 'error' });
            }
        } catch (error) {
            console.error('Error fetching users:', error);
            setToast({ open: true, message: 'Error fetching clients', severity: 'error' });
        }
    };

    useEffect(() => {
        fetchProjects();
        fetchUsers();
    }, []);

    const handleAddProject = () => {
        setEditingProject(null);
        setFormData({
            name: '',
            userId: '',
            status: 'Planning',
            progress: 0,
            dueDate: new Date().toISOString().split('T')[0],
            team: '',
            description: ''
        });
        setOpenDialog(true);
    };

    const handleEditProject = (project: Project) => {
        setEditingProject(project);
        setFormData({
            name: project.name,
            userId: typeof project.userId === 'object' ? project.userId._id : project.userId,
            status: project.status,
            progress: project.progress,
            dueDate: project.dueDate ? new Date(project.dueDate).toISOString().split('T')[0] : '',
            team: project.team,
            description: project.description
        });
        setOpenDialog(true);
    };

    const handleDeleteProject = async (id: string) => {
        if (!confirm('Are you sure you want to delete this project?')) return;

        try {
            const res = await fetch(`/api/projects/${id}`, { method: 'DELETE', credentials: 'include' });
            if (res.ok) {
                setToast({ open: true, message: 'Project deleted successfully', severity: 'success' });
                setProjects(projects.filter(p => p._id !== id));
            } else {
                setToast({ open: true, message: 'Failed to delete project', severity: 'error' });
            }
        } catch (error) {
            setToast({ open: true, message: 'Error deleting project', severity: 'error' });
        }
    };

    const handleSave = async () => {
        if (!formData.name || !formData.userId || !formData.dueDate) {
            setToast({ open: true, message: 'Please fill in all required fields', severity: 'error' });
            return;
        }

        setActionLoading(true);
        try {
            const url = editingProject ? `/api/projects/${editingProject._id}` : '/api/projects';
            const method = editingProject ? 'PUT' : 'POST';

            const res = await fetch(url, {
                method,
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData),
                credentials: 'include'
            });

            if (res.ok) {
                setToast({ open: true, message: `Project ${editingProject ? 'updated' : 'created'} successfully`, severity: 'success' });
                setOpenDialog(false);
                fetchProjects();
            } else {
                const error = await res.json();
                setToast({ open: true, message: error.error || 'Operation failed', severity: 'error' });
            }
        } catch (error) {
            setToast({ open: true, message: 'An error occurred', severity: 'error' });
        } finally {
            setActionLoading(false);
        }
    };

    const getStatusColor = (status: string) => {
        switch (status) {
            case 'In Progress': return 'primary';
            case 'Review': return 'warning';
            case 'Planning': return 'info';
            case 'Completed': return 'success';
            default: return 'default';
        }
    };

    return (
        <Stack spacing={3}>
            <Box display="flex" justifyContent="space-between" alignItems="center">
                <Box>
                    <Typography variant="h5" fontWeight="bold">
                        Projects Management
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        Manage client projects displayed in user dashboards
                    </Typography>
                </Box>
                <Button
                    variant="contained"
                    startIcon={<Plus size={18} />}
                    onClick={handleAddProject}
                    sx={{
                        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                        textTransform: 'none',
                        borderRadius: '12px',
                    }}
                >
                    Add Project
                </Button>
            </Box>

            <Paper elevation={0} sx={{ borderRadius: '16px', border: '1px solid #e2e8f0', overflow: 'hidden' }}>
                <TableContainer>
                    {loading ? (
                        <Box display="flex" justifyContent="center" p={4}>
                            <CircularProgress />
                        </Box>
                    ) : (
                        <Table>
                            <TableHead sx={{ bgcolor: '#f8fafc' }}>
                                <TableRow>
                                    <TableCell sx={{ fontWeight: 600 }}>Project Name</TableCell>
                                    <TableCell sx={{ fontWeight: 600 }}>Client</TableCell>
                                    <TableCell sx={{ fontWeight: 600 }}>Status</TableCell>
                                    <TableCell sx={{ fontWeight: 600 }}>Progress</TableCell>
                                    <TableCell sx={{ fontWeight: 600 }}>Due Date</TableCell>
                                    <TableCell sx={{ fontWeight: 600 }}>Team</TableCell>
                                    <TableCell align="right" sx={{ fontWeight: 600 }}>Actions</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {projects.map((project) => (
                                    <TableRow key={project._id} hover>
                                        <TableCell>
                                            <Typography variant="subtitle2" fontWeight={600}>
                                                {project.name}
                                            </Typography>
                                        </TableCell>
                                        <TableCell>
                                            {typeof project.userId === 'object' ? project.userId?.name : 'Unknown User'}
                                            <Typography variant="caption" display="block" color="text.secondary">
                                                {typeof project.userId === 'object' ? project.userId?.email : ''}
                                            </Typography>
                                        </TableCell>
                                        <TableCell>
                                            <Chip
                                                label={project.status}
                                                size="small"
                                                color={getStatusColor(project.status) as any}
                                                sx={{ borderRadius: '6px' }}
                                            />
                                        </TableCell>
                                        <TableCell>{project.progress}%</TableCell>
                                        <TableCell>{new Date(project.dueDate).toLocaleDateString()}</TableCell>
                                        <TableCell>{project.team}</TableCell>
                                        <TableCell align="right">
                                            <Stack direction="row" spacing={1} justifyContent="flex-end">
                                                <IconButton size="small" onClick={() => handleEditProject(project)}>
                                                    <Edit size={16} />
                                                </IconButton>
                                                <IconButton size="small" color="error" onClick={() => handleDeleteProject(project._id)}>
                                                    <Trash2 size={16} />
                                                </IconButton>
                                            </Stack>
                                        </TableCell>
                                    </TableRow>
                                ))}
                                {projects.length === 0 && (
                                    <TableRow>
                                        <TableCell colSpan={7} align="center" sx={{ py: 3, color: 'text.secondary' }}>
                                            No projects found. Create one to get started.
                                        </TableCell>
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                    )}
                </TableContainer>
            </Paper>

            {/* Add/Edit Dialog */}
            <Dialog open={openDialog} onClose={() => setOpenDialog(false)} maxWidth="md" fullWidth>
                <DialogTitle>{editingProject ? 'Edit Project' : 'Add New Project'}</DialogTitle>
                <DialogContent>
                    <Stack spacing={2} sx={{ mt: 1 }}>
                        <TextField
                            fullWidth
                            label="Project Name"
                            value={formData.name}
                            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                            required
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                        <TextField
                            fullWidth
                            select
                            label="Client"
                            value={formData.userId}
                            onChange={(e) => setFormData({ ...formData, userId: e.target.value })}
                            required
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        >
                            {users.length > 0 ? (
                                users.map((user) => (
                                    <MenuItem key={user._id} value={user._id}>
                                        {user.name} ({user.email})
                                    </MenuItem>
                                ))
                            ) : (
                                <MenuItem value="" disabled>
                                    No clients found
                                </MenuItem>
                            )}
                        </TextField>
                        <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
                            <TextField
                                fullWidth
                                select
                                label="Status"
                                value={formData.status}
                                onChange={(e) => setFormData({ ...formData, status: e.target.value as any })}
                                sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                            >
                                <MenuItem value="Planning">Planning</MenuItem>
                                <MenuItem value="In Progress">In Progress</MenuItem>
                                <MenuItem value="Review">Review</MenuItem>
                                <MenuItem value="Completed">Completed</MenuItem>
                            </TextField>
                            <TextField
                                fullWidth
                                type="number"
                                label="Progress (%)"
                                value={formData.progress}
                                onChange={(e) => setFormData({ ...formData, progress: Number(e.target.value) })}
                                inputProps={{ min: 0, max: 100 }}
                                sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                            />
                        </Stack>
                        <Stack direction={{ xs: 'column', sm: 'row' }} spacing={2}>
                            <TextField
                                fullWidth
                                type="date"
                                label="Due Date"
                                value={formData.dueDate}
                                onChange={(e) => setFormData({ ...formData, dueDate: e.target.value })}
                                InputLabelProps={{ shrink: true }}
                                required
                                sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                            />
                            <TextField
                                fullWidth
                                label="Team"
                                value={formData.team}
                                onChange={(e) => setFormData({ ...formData, team: e.target.value })}
                                sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                            />
                        </Stack>
                        <TextField
                            fullWidth
                            multiline
                            rows={3}
                            label="Description"
                            value={formData.description}
                            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Stack>
                </DialogContent>
                <DialogActions sx={{ p: 2 }}>
                    <Button onClick={() => setOpenDialog(false)} sx={{ textTransform: 'none' }}>
                        Cancel
                    </Button>
                    <Button
                        variant="contained"
                        onClick={handleSave}
                        disabled={actionLoading}
                        sx={{
                            textTransform: 'none',
                            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                        }}
                    >
                        {actionLoading ? 'Saving...' : (editingProject ? 'Update Project' : 'Create Project')}
                    </Button>
                </DialogActions>
            </Dialog>

            {/* Notification Toast */}
            <Snackbar
                open={toast.open}
                autoHideDuration={6000}
                onClose={() => setToast({ ...toast, open: false })}
                anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
            >
                <Alert onClose={() => setToast({ ...toast, open: false })} severity={toast.severity} sx={{ width: '100%' }}>
                    {toast.message}
                </Alert>
            </Snackbar>
        </Stack>
    );
}

import { useState } from 'react';
import {
    Box, Paper, Typography, Stack, Switch, Table, TableBody, TableCell,
    TableContainer, TableHead, TableRow, Chip, Button, List, ListItem,
    ListItemText, ListItemButton, Divider
} from '@mui/material';
import {
    Shield, Lock, Users, Save, CheckCircle, AlertTriangle
} from 'lucide-react';

const ROLES = [
    { id: 'super_admin', label: 'Super Admin', color: 'error', description: 'Full system access' },
    { id: 'admin', label: 'Admin', color: 'primary', description: 'Manage content and users' },
    { id: 'moderator', label: 'Moderator', color: 'warning', description: 'Review and approve content' },
    { id: 'support', label: 'Support', color: 'info', description: 'View only access' },
];

const MODULES = [
    { id: 'users', label: 'User Management' },
    { id: 'content', label: 'Content / Data' },
    { id: 'transactions', label: 'Transactions' },
    { id: 'settings', label: 'System Settings' },
];

const INITIAL_PERMISSIONS = {
    super_admin: { users: ['read', 'write', 'delete'], content: ['read', 'write', 'delete'], transactions: ['read', 'write', 'delete'], settings: ['read', 'write', 'delete'] },
    admin: { users: ['read', 'write', 'delete'], content: ['read', 'write', 'delete'], transactions: ['read', 'write'], settings: ['read'] },
    moderator: { users: ['read'], content: ['read', 'write'], transactions: [], settings: [] },
    support: { users: ['read'], content: ['read'], transactions: ['read'], settings: [] },
} as any;

export default function RolesSection() {
    const [selectedRole, setSelectedRole] = useState('admin');
    const [permissions, setPermissions] = useState(INITIAL_PERMISSIONS);
    const [saving, setSaving] = useState(false);

    const handlePermissionToggle = (moduleIdx: string, type: string) => {
        setPermissions((prev: any) => {
            const rolePerms = prev[selectedRole] || {};
            const modulePerms = rolePerms[moduleIdx] || [];

            let newModulePerms;
            if (modulePerms.includes(type)) {
                newModulePerms = modulePerms.filter((p: string) => p !== type);
            } else {
                newModulePerms = [...modulePerms, type];
            }

            return {
                ...prev,
                [selectedRole]: {
                    ...rolePerms,
                    [moduleIdx]: newModulePerms
                }
            };
        });
    };

    const handleSave = () => {
        setSaving(true);
        setTimeout(() => setSaving(false), 1000);
    };

    const currentRole = ROLES.find(r => r.id === selectedRole);

    return (
        <Stack spacing={3}>
            {/* Header */}
            <Paper elevation={0} sx={{ p: 2, borderRadius: '16px', border: '1px solid #e2e8f0' }}>
                <Box display="flex" justifyContent="space-between" alignItems="center">
                    <Box>
                        <Typography variant="h6" fontWeight="bold">Roles & Permissions</Typography>
                        <Typography variant="body2" color="text.secondary">Manage system access levels</Typography>
                    </Box>
                    <Button
                        variant="contained"
                        startIcon={saving ? <CheckCircle size={18} /> : <Save size={18} />}
                        onClick={handleSave}
                        disabled={saving}
                        sx={{ bgcolor: saving ? 'success.main' : 'primary.main', textTransform: 'none' }}
                    >
                        {saving ? 'Saved Changes' : 'Save Changes'}
                    </Button>
                </Box>
            </Paper>

            <Box display="grid" gridTemplateColumns={{ xs: '1fr', md: '280px 1fr' }} gap={3}>
                {/* Roles List Sidebar */}
                <Paper elevation={0} sx={{ borderRadius: '16px', border: '1px solid #e2e8f0', overflow: 'hidden', height: 'fit-content' }}>
                    <Box p={2} bgcolor="#f8fafc" borderBottom="1px solid #e2e8f0">
                        <Typography variant="subtitle2" fontWeight={600} color="text.secondary">AVAILABLE ROLES</Typography>
                    </Box>
                    <List sx={{ p: 0 }}>
                        {ROLES.map((role) => (
                            <div key={role.id}>
                                <ListItem disablePadding>
                                    <ListItemButton
                                        selected={selectedRole === role.id}
                                        onClick={() => setSelectedRole(role.id)}
                                        sx={{
                                            p: 2,
                                            '&.Mui-selected': { bgcolor: `${role.color}.50`, borderLeft: `4px solid`, borderColor: `${role.color}.main` }
                                        }}
                                    >
                                        <ListItemText
                                            primary={
                                                <Box display="flex" alignItems="center" justifyContent="space-between">
                                                    <Typography fontWeight={selectedRole === role.id ? 600 : 500}>{role.label}</Typography>
                                                    {role.id === 'super_admin' && <Shield size={14} color="#ef4444" />}
                                                </Box>
                                            }
                                            secondary={role.description}
                                            secondaryTypographyProps={{ variant: 'caption', noWrap: true }}
                                        />
                                    </ListItemButton>
                                </ListItem>
                                <Divider component="li" />
                            </div>
                        ))}
                    </List>
                    <Box p={2}>
                        <Button fullWidth variant="outlined" startIcon={<Users size={16} />} sx={{ textTransform: 'none' }}>
                            Add New Role
                        </Button>
                    </Box>
                </Paper>

                {/* Permissions Matrix */}
                <Paper elevation={0} sx={{ borderRadius: '16px', border: '1px solid #e2e8f0', overflow: 'hidden' }}>
                    <Box p={3} borderBottom="1px solid #e2e8f0" display="flex" justifyContent="space-between" alignItems="center">
                        <Box display="flex" alignItems="center" gap={2}>
                            <Typography variant="h6" fontWeight="bold">Permissions for {currentRole?.label}</Typography>
                            <Chip
                                label={currentRole?.id.toUpperCase().replace('_', ' ')}
                                size="small"
                                color={currentRole?.color as any}
                                variant="outlined"
                                sx={{ borderRadius: '6px', fontWeight: 600 }}
                            />
                        </Box>
                        {selectedRole === 'super_admin' && (
                            <Box display="flex" alignItems="center" gap={1} color="warning.main" bgcolor="#fff7ed" p={1} borderRadius="8px">
                                <AlertTriangle size={16} />
                                <Typography variant="caption" fontWeight={600}>Super Admin has full access by default</Typography>
                            </Box>
                        )}
                    </Box>

                    <TableContainer>
                        <Table>
                            <TableHead sx={{ bgcolor: '#f8fafc' }}>
                                <TableRow>
                                    <TableCell sx={{ fontWeight: 600, color: 'text.secondary' }}>Module Access</TableCell>
                                    <TableCell align="center" sx={{ fontWeight: 600, color: 'text.secondary', width: 100 }}>Read</TableCell>
                                    <TableCell align="center" sx={{ fontWeight: 600, color: 'text.secondary', width: 100 }}>Write</TableCell>
                                    <TableCell align="center" sx={{ fontWeight: 600, color: 'text.secondary', width: 100 }}>Delete</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {MODULES.map((module) => {
                                    const rolePerms = permissions[selectedRole]?.[module.id] || [];
                                    const isSuper = selectedRole === 'super_admin';

                                    return (
                                        <TableRow key={module.id} hover>
                                            <TableCell>
                                                <Typography variant="subtitle2" fontWeight={600}>{module.label}</Typography>
                                            </TableCell>
                                            {['read', 'write', 'delete'].map((type) => (
                                                <TableCell key={type} align="center">
                                                    <Switch
                                                        size="small"
                                                        checked={rolePerms.includes(type)}
                                                        onChange={() => handlePermissionToggle(module.id, type)}
                                                        disabled={isSuper} // Super admin always enabled conceptually, or just locked
                                                        color={
                                                            type === 'delete' ? 'error' :
                                                                type === 'write' ? 'secondary' : 'primary'
                                                        }
                                                    />
                                                </TableCell>
                                            ))}
                                        </TableRow>
                                    );
                                })}
                            </TableBody>
                        </Table>
                    </TableContainer>

                    <Box p={3} bgcolor="#f8fafc">
                        <Typography variant="caption" color="text.secondary" display="block" align="center">
                            Note: Changes to permissions may take a few minutes to reflect for logged-in users.
                        </Typography>
                    </Box>
                </Paper>
            </Box>
        </Stack>
    );
}

'use client';

import { Box, Grid, TextField, Paper, Typography } from '@mui/material';

interface ServicesSectionProps {
    data: any;
    handleChange: (field: string, value: any) => void;
    handleArrayChange: (arrayName: string, index: number, field: string, value: any) => void;
}

export default function ServicesSection({ data, handleChange, handleArrayChange }: ServicesSectionProps) {
    return (
        <Box>
            <Grid container spacing={3} mb={4}>
                <Grid item xs={12}>
                    <TextField
                        fullWidth
                        label="Section Heading"
                        value={data.heading}
                        onChange={(e) => handleChange('heading', e.target.value)}
                        sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px', bgcolor: 'white' } }}
                    />
                </Grid>
                <Grid item xs={12}>
                    <TextField
                        fullWidth
                        label="Sub Heading"
                        value={data.subHeading}
                        onChange={(e) => handleChange('subHeading', e.target.value)}
                        sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px', bgcolor: 'white' } }}
                    />
                </Grid>
            </Grid>

            {data.items.map((item: any, index: number) => (
                <Paper
                    key={index}
                    variant="outlined"
                    sx={{
                        mb: 3,
                        p: 3,
                        borderRadius: '16px',
                        bgcolor: 'white',
                        border: '1px solid #e2e8f0',
                        transition: 'box-shadow 0.2s',
                        '&:hover': {
                            boxShadow: '0 4px 12px rgba(0,0,0,0.05)'
                        }
                    }}
                >
                    <Typography variant="subtitle2" mb={2} color="primary" fontWeight="bold">
                        Service {index + 1}
                    </Typography>
                    <Grid container spacing={2}>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                fullWidth
                                label="Title"
                                size="small"
                                value={item.title}
                                onChange={(e) => handleArrayChange('items', index, 'title', e.target.value)}
                            />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                            <TextField
                                fullWidth
                                label="Icon (Lucide Name)"
                                size="small"
                                value={item.icon}
                                onChange={(e) => handleArrayChange('items', index, 'icon', e.target.value)}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                fullWidth
                                multiline
                                rows={2}
                                label="Description"
                                size="small"
                                value={item.description}
                                onChange={(e) => handleArrayChange('items', index, 'description', e.target.value)}
                            />
                        </Grid>
                    </Grid>
                </Paper>
            ))}
        </Box>
    );
}

'use client';

import { useState, useRef } from 'react';
import { Grid, TextField, Box, Popover, ClickAwayListener } from '@mui/material';
import { HexColorPicker } from 'react-colorful';

interface SettingsSectionProps {
    data: any;
    handleChange: (field: string, value: any) => void;
}

export default function SettingsSection({ data, handleChange }: SettingsSectionProps) {
    const [activeColorField, setActiveColorField] = useState<string | null>(null);

    const handleColorClick = (event: React.MouseEvent<HTMLElement>, field: string) => {
        setAnchorEl(event.currentTarget);
        setActiveColorField(field);
    };

    const handleClose = () => {
        setAnchorEl(null);
        setActiveColorField(null);
    };

    const open = Boolean(anchorEl);

    const renderColorPicker = (label: string, field: string) => (
        <Grid size={{ xs: 12, sm: 6 }}>
            <Box display="flex" gap={2} alignItems="center">
                <TextField
                    fullWidth
                    label={label}
                    variant="outlined"
                    value={data[field] || ''}
                    onChange={(e) => handleChange(field, e.target.value)}
                    sx={{
                        '& .MuiOutlinedInput-root': {
                            borderRadius: '12px',
                            bgcolor: 'white'
                        }
                    }}
                />
                <Box
                    onClick={(e) => handleColorClick(e, field)}
                    sx={{
                        width: 56,
                        height: 56,
                        borderRadius: '12px',
                        bgcolor: data[field],
                        border: '1px solid #e2e8f0',
                        flexShrink: 0,
                        boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
                        cursor: 'pointer',
                        transition: 'transform 0.2s',
                        '&:hover': { transform: 'scale(1.05)' }
                    }}
                />
            </Box>
        </Grid>
    );

    return (
        <Grid container spacing={3}>
            <Grid size={{ xs: 12 }}>
                <TextField
                    fullWidth
                    label="Site Title"
                    variant="outlined"
                    value={data.title}
                    onChange={(e) => handleChange('title', e.target.value)}
                    sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px', bgcolor: 'white' } }}
                />
            </Grid>
            <Grid size={{ xs: 12 }}>
                <TextField
                    fullWidth
                    label="Site Description"
                    variant="outlined"
                    value={data.description}
                    onChange={(e) => handleChange('description', e.target.value)}
                    sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px', bgcolor: 'white' } }}
                />
            </Grid>

            {renderColorPicker("Primary Color", "themeColor")}
            {renderColorPicker("Secondary Color", "secondaryColor")}
            {renderColorPicker("Page Background", "backgroundColor")}
            {renderColorPicker("Card/Paper Background", "paperColor")}

            <Popover
                open={open}
                anchorEl={anchorEl}
                onClose={handleClose}
                anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }}
                transformOrigin={{ vertical: 'top', horizontal: 'left' }}
                PaperProps={{ sx: { p: 2, borderRadius: '16px', boxShadow: '0 4px 20px rgba(0,0,0,0.1)' } }}
            >
                {activeColorField && (
                    <HexColorPicker
                        color={data[activeColorField] || '#ffffff'}
                        onChange={(newColor) => handleChange(activeColorField, newColor)}
                    />
                )}
            </Popover>
        </Grid>
    );
}

import { useState, useEffect } from 'react';
import {
    Box, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
    IconButton, Menu, MenuItem, Chip, Typography, Stack, Grid, Card, CardContent,
    Button, Divider, Dialog, DialogTitle, DialogContent, TextField, DialogActions
} from '@mui/material';
import {
    MoreVertical, Download, RotateCcw, CheckCircle, AlertCircle, Clock,
    DollarSign, TrendingUp, CreditCard, Search, Plus
} from 'lucide-react';

// Define Transaction Interface matching our new Model
interface Transaction {
    _id: string;
    user: any;
    amount: number;
    currency: string;
    method?: string;
    status: 'Success' | 'Failed' | 'Pending';
    razorpayOrderId?: string;
    description?: string;
    createdAt: string;
}

export default function TransactionsSection() {
    const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
    const [selectedTxn, setSelectedTxn] = useState<Transaction | null>(null);
    const [transactions, setTransactions] = useState<Transaction[]>([]);
    const [loading, setLoading] = useState(true);

    // Payment Dialog State
    const [openPayment, setOpenPayment] = useState(false);
    const [paymentAmount, setPaymentAmount] = useState('');
    const [paymentDescription, setPaymentDescription] = useState('');
    const [processing, setProcessing] = useState(false);

    // Fetch Transactions (TODO: Implement GET /api/transactions endpoint later, for now we just show what we create or mock)
    // Simulating empty list for now until next step or just assume we add to list manually after payment

    const handleMenuOpen = (event: React.MouseEvent<HTMLElement>, txn: Transaction) => {
        setAnchorEl(event.currentTarget);
        setSelectedTxn(txn);
    };

    const handleMenuClose = () => {
        setAnchorEl(null);
        setSelectedTxn(null);
    };

    const getStatusColor = (status: string) => {
        switch (status) {
            case 'Success': return 'success';
            case 'Failed': return 'error';
            case 'Pending': return 'warning';
            default: return 'default';
        }
    };

    // Load Razorpay Script
    const loadRazorpay = () => {
        return new Promise((resolve) => {
            const script = document.createElement('script');
            script.src = 'https://checkout.razorpay.com/v1/checkout.js';
            script.onload = () => resolve(true);
            script.onerror = () => resolve(false);
            document.body.appendChild(script);
        });
    };

    const handlePayment = async () => {
        if (!paymentAmount) return;

        setProcessing(true);
        try {
            // 1. Load Script
            const isLoaded = await loadRazorpay();
            if (!isLoaded) {
                alert('Razorpay SDK failed to load');
                return;
            }

            // 2. Create Order
            const res = await fetch('/api/payment/order', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    amount: parseFloat(paymentAmount),
                    description: paymentDescription
                }),
                credentials: 'include'
            });

            const orderData = await res.json();
            if (!res.ok) throw new Error(orderData.error);

            // 3. Open Razorpay Options
            const options = {
                key: process.env.NEXT_PUBLIC_RAZORPAY_KEY_ID || 'rzp_test_placeholder', // Should be in env
                amount: orderData.amount,
                currency: orderData.currency,
                name: 'SDEVX Tech',
                description: paymentDescription || 'Transaction',
                order_id: orderData.id,
                handler: async function (response: any) {
                    // 4. Verify Payment
                    try {
                        const verifyRes = await fetch('/api/payment/verify', {
                            method: 'POST',
                            headers: { 'Content-Type': 'application/json' },
                            body: JSON.stringify({
                                razorpay_order_id: response.razorpay_order_id,
                                razorpay_payment_id: response.razorpay_payment_id,
                                razorpay_signature: response.razorpay_signature
                            }),
                            credentials: 'include'
                        });

                        const verifyData = await verifyRes.json();
                        if (verifyRes.ok) {
                            alert('Payment Successful!');
                            setOpenPayment(false);
                            // Ideally fetch transactions here
                            // fetchTransactions();

                            // Mock update list for immediate feedback
                            const newTxn: Transaction = {
                                _id: orderData.id, // temp id
                                user: 'Current User',
                                amount: parseFloat(paymentAmount),
                                currency: 'INR',
                                status: 'Success',
                                description: paymentDescription,
                                createdAt: new Date().toISOString()
                            };
                            setTransactions([newTxn, ...transactions]);
                        } else {
                            alert('Payment Verification Failed');
                        }
                    } catch (err) {
                        console.error(err);
                        alert('Verification Error');
                    }
                },
                prefill: {
                    name: 'Admin User',
                    email: 'admin@test.com',
                },
                theme: {
                    color: '#6366f1',
                },
            };

            const paymentObject = new (window as any).Razorpay(options);
            paymentObject.open();

        } catch (error: any) {
            console.error('Payment Error:', error);
            alert(error.message || 'Payment Failed');
        } finally {
            setProcessing(false);
        }
    };

    return (
        <Stack spacing={3}>
            {/* Overview Stats (Keep Mock for Visuals for now or connect later) */}
            <Stack direction={{ xs: 'column', sm: 'row' }} spacing={3}>
                <Box flex={1}>
                    <Card elevation={0} sx={{ borderRadius: '16px', border: '1px solid #e2e8f0', bgcolor: 'primary.main', color: 'white', height: '100%' }}>
                        <CardContent>
                            <Box display="flex" justifyContent="space-between" alignItems="flex-start">
                                <Box>
                                    <Typography variant="body2" sx={{ opacity: 0.8, mb: 1 }}>Total Revenue</Typography>
                                    <Typography variant="h4" fontWeight="bold">₹45,230</Typography>
                                </Box>
                                <Box p={1} bgcolor="rgba(255,255,255,0.2)" borderRadius="12px">
                                    <DollarSign size={24} color="white" />
                                </Box>
                            </Box>
                        </CardContent>
                    </Card>
                </Box>
                {/* ... other stats ... */}
            </Stack>

            {/* Transactions Table */}
            <Paper elevation={0} sx={{ borderRadius: '16px', border: '1px solid #e2e8f0', overflow: 'hidden' }}>
                <Box p={2} borderBottom="1px solid #e2e8f0" display="flex" justifyContent="space-between" alignItems="center">
                    <Typography variant="h6" fontWeight="bold">Transactions</Typography>
                    <Stack direction="row" spacing={2}>
                        <Button
                            variant="contained"
                            startIcon={<Plus size={16} />}
                            onClick={() => setOpenPayment(true)}
                            sx={{ textTransform: 'none' }}
                        >
                            New Transaction
                        </Button>
                    </Stack>
                </Box>
                <TableContainer>
                    <Table>
                        <TableHead sx={{ bgcolor: '#f8fafc' }}>
                            <TableRow>
                                <TableCell sx={{ fontWeight: 600 }}>Date</TableCell>
                                <TableCell sx={{ fontWeight: 600 }}>Description</TableCell>
                                <TableCell sx={{ fontWeight: 600 }}>Amount</TableCell>
                                <TableCell sx={{ fontWeight: 600 }}>Status</TableCell>
                                <TableCell align="right" sx={{ fontWeight: 600 }}>Actions</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {transactions.length === 0 ? (
                                <TableRow>
                                    <TableCell colSpan={5} align="center" sx={{ py: 4, color: 'text.secondary' }}>
                                        No recent transactions. Create one to test Razorpay.
                                    </TableCell>
                                </TableRow>
                            ) : (
                                transactions.map((txn) => (
                                    <TableRow key={txn._id} hover>
                                        <TableCell>{new Date(txn.createdAt).toLocaleDateString()}</TableCell>
                                        <TableCell>{txn.description || 'Payment'}</TableCell>
                                        <TableCell>₹{txn.amount}</TableCell>
                                        <TableCell>
                                            <Chip
                                                label={txn.status}
                                                size="small"
                                                color={getStatusColor(txn.status) as any}
                                                variant="outlined"
                                            />
                                        </TableCell>
                                        <TableCell align="right">
                                            <IconButton size="small"><MoreVertical size={16} /></IconButton>
                                        </TableCell>
                                    </TableRow>
                                ))
                            )}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Paper>

            {/* Payment Dialog */}
            <Dialog open={openPayment} onClose={() => setOpenPayment(false)} maxWidth="sm" fullWidth>
                <DialogTitle>Initiate Payment</DialogTitle>
                <DialogContent>
                    <Stack spacing={3} sx={{ mt: 1 }}>
                        <TextField
                            label="Amount (INR)"
                            type="number"
                            fullWidth
                            value={paymentAmount}
                            onChange={(e) => setPaymentAmount(e.target.value)}
                        />
                        <TextField
                            label="Description"
                            fullWidth
                            value={paymentDescription}
                            onChange={(e) => setPaymentDescription(e.target.value)}
                        />
                    </Stack>
                </DialogContent>
                <DialogActions sx={{ p: 2 }}>
                    <Button onClick={() => setOpenPayment(false)}>Cancel</Button>
                    <Button
                        variant="contained"
                        onClick={handlePayment}
                        disabled={processing || !paymentAmount}
                    >
                        {processing ? 'Processing...' : 'Pay Now'}
                    </Button>
                </DialogActions>
            </Dialog>
        </Stack>
    );
}

import { useState, useEffect } from 'react';
import {
    Box, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
    IconButton, Menu, MenuItem, Chip, Avatar, Typography, Tooltip, Stack,
    TextField, InputAdornment, Button, Divider, CircularProgress, Dialog,
    DialogTitle, DialogContent, DialogActions, Grid, FormControl, InputLabel, Select, Snackbar, Alert
} from '@mui/material';
import {
    MoreVertical, Eye, Edit, Ban, Trash2, Key, Shield, Search, Filter, Plus, CheckCircle
} from 'lucide-react';

interface User {
    _id: string; // MongoDB ID
    name: string;
    email: string;
    role: 'user' | 'admin';
    createdAt: string;
    // UI only fields (optional or derived)
    phone?: string;
    status?: string;
    avatar?: string;
}

export default function UsersSection() {
    const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
    const [selectedUser, setSelectedUser] = useState<User | null>(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [users, setUsers] = useState<User[]>([]);
    const [loading, setLoading] = useState(true);

    // Action States
    const [openAddDialog, setOpenAddDialog] = useState(false);
    const [actionLoading, setActionLoading] = useState(false);
    const [toast, setToast] = useState({ open: false, message: '', severity: 'success' as 'success' | 'error' });

    // New User Form State
    const [newUser, setNewUser] = useState({
        name: '',
        email: '',
        password: '',
        role: 'user',
        phone: ''
    });

    const fetchUsers = async () => {
        try {
            const response = await fetch('/api/users', {
                credentials: 'include'
            });
            if (response.ok) {
                const data = await response.json();
                setUsers(data.users || []);
            } else {
                if (response.status === 403) {
                    setToast({ open: true, message: 'Access Denied: You need admin privileges to view users.', severity: 'error' });
                } else {
                    console.error('Failed to fetch users', await response.text());
                    setToast({ open: true, message: 'Failed to fetch users', severity: 'error' });
                }
            }
        } catch (error) {
            console.error('Error loading users:', error);
            setToast({ open: true, message: 'Error loading users list', severity: 'error' });
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    const handleMenuOpen = (event: React.MouseEvent<HTMLElement>, user: User) => {
        setAnchorEl(event.currentTarget);
        setSelectedUser(user);
    };

    const handleMenuClose = () => {
        setAnchorEl(null);
        setSelectedUser(null);
    };

    // API Actions
    const handleAddUser = async () => {
        if (!newUser.name || !newUser.email || !newUser.password) {
            setToast({ open: true, message: 'Please fill in all required fields', severity: 'error' });
            return;
        }

        setActionLoading(true);
        try {
            const res = await fetch('/api/users', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(newUser),
                credentials: 'include'
            });

            if (res.ok) {
                setToast({ open: true, message: 'User created successfully', severity: 'success' });
                setOpenAddDialog(false);
                setNewUser({ name: '', email: '', password: '', role: 'user', phone: '' });
                fetchUsers();
            } else {
                const error = await res.json();
                setToast({ open: true, message: error.error || 'Failed to create user', severity: 'error' });
            }
        } catch (error) {
            setToast({ open: true, message: 'Error creating user', severity: 'error' });
        } finally {
            setActionLoading(false);
        }
    };

    const handleUpdateUserStatus = async (status: string) => {
        if (!selectedUser) return;
        handleMenuClose();

        // Optimistic update
        const updatedUsers = users.map(u =>
            u._id === selectedUser._id ? { ...u, status } : u
        );
        setUsers(updatedUsers);

        try {
            const res = await fetch(`/api/users/${selectedUser._id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ status }),
                credentials: 'include'
            });

            if (!res.ok) {
                const error = await res.json();
                setToast({ open: true, message: error.error || 'Failed to update status', severity: 'error' });
                fetchUsers(); // Revert
            } else {
                setToast({ open: true, message: `User ${status === 'Blocked' ? 'blocked' : 'activated'}`, severity: 'success' });
            }
        } catch (error) {
            setToast({ open: true, message: 'Error updating user', severity: 'error' });
            fetchUsers();
        }
    };

    const handleDeleteUser = async () => {
        if (!selectedUser) return;
        handleMenuClose();

        if (!confirm('Are you sure you want to delete this user? This action cannot be undone.')) return;

        try {
            const res = await fetch(`/api/users/${selectedUser._id}`, {
                method: 'DELETE',
                credentials: 'include'
            });

            if (res.ok) {
                setToast({ open: true, message: 'User deleted successfully', severity: 'success' });
                setUsers(users.filter(u => u._id !== selectedUser._id));
            } else {
                const error = await res.json();
                setToast({ open: true, message: error.error || 'Failed to delete user', severity: 'error' });
            }
        } catch (error) {
            setToast({ open: true, message: 'Error deleting user', severity: 'error' });
        }
    };

    const handleChangeRole = async () => {
        if (!selectedUser) return;
        handleMenuClose();

        const newRole = selectedUser.role === 'admin' ? 'user' : 'admin';

        try {
            const res = await fetch(`/api/users/${selectedUser._id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ role: newRole }),
                credentials: 'include'
            });

            if (res.ok) {
                setToast({ open: true, message: `Role updated to ${newRole}`, severity: 'success' });
                fetchUsers();
            } else {
                const error = await res.json();
                setToast({ open: true, message: error.error || 'Failed to update role', severity: 'error' });
            }
        } catch (error) {
            setToast({ open: true, message: 'Error updating role', severity: 'error' });
        }
    };

    const getStatusColor = (status: string) => {
        switch (status) {
            case 'Active': return 'success';
            case 'Blocked': return 'error';
            case 'Pending': return 'warning';
            default: return 'success';
        }
    };

    const getRoleColor = (role: string) => {
        switch (role) {
            case 'admin': return 'primary.main';
            case 'user': return 'text.secondary';
            default: return 'text.secondary';
        }
    };

    // Filter users
    const filteredUsers = users.filter(user =>
        user.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        user.email?.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <Stack spacing={3}>
            {/* Toolbar */}
            <Paper elevation={0} sx={{ p: 2, borderRadius: '16px', border: '1px solid #e2e8f0' }}>
                <Box display="flex" justifyContent="space-between" alignItems="center" gap={2} flexWrap="wrap">
                    <TextField
                        size="small"
                        placeholder="Search users..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        InputProps={{
                            startAdornment: (
                                <InputAdornment position="start">
                                    <Search size={18} color="#94a3b8" />
                                </InputAdornment>
                            ),
                        }}
                        sx={{ width: { xs: '100%', sm: 300 } }}
                    />
                    <Stack direction="row" spacing={2}>
                        <Button
                            variant="outlined"
                            startIcon={<Filter size={18} />}
                            sx={{ borderColor: '#e2e8f0', color: 'text.secondary', textTransform: 'none' }}
                        >
                            Filters
                        </Button>
                        <Button
                            variant="contained"
                            startIcon={<Plus size={18} />}
                            onClick={() => setOpenAddDialog(true)}
                            sx={{ bgcolor: 'primary.main', textTransform: 'none', borderRadius: '8px' }}
                        >
                            Add User
                        </Button>
                    </Stack>
                </Box>
            </Paper>

            {/* Users Table */}
            <Paper elevation={0} sx={{ borderRadius: '16px', border: '1px solid #e2e8f0', overflow: 'hidden' }}>
                <TableContainer>
                    {loading ? (
                        <Box display="flex" justifyContent="center" p={4}>
                            <CircularProgress />
                        </Box>
                    ) : (
                        <Table>
                            <TableHead sx={{ bgcolor: '#f8fafc' }}>
                                <TableRow>
                                    <TableCell sx={{ fontWeight: 600, color: 'text.secondary' }}>User</TableCell>
                                    <TableCell sx={{ fontWeight: 600, color: 'text.secondary' }}>Contact</TableCell>
                                    <TableCell sx={{ fontWeight: 600, color: 'text.secondary' }}>Role</TableCell>
                                    <TableCell sx={{ fontWeight: 600, color: 'text.secondary' }}>Status</TableCell>
                                    <TableCell sx={{ fontWeight: 600, color: 'text.secondary' }}>Created</TableCell>
                                    <TableCell align="right" sx={{ fontWeight: 600, color: 'text.secondary' }}>Actions</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {filteredUsers.map((user) => (
                                    <TableRow key={user._id} hover sx={{ '&:last-child td, &:last-child th': { border: 0 } }}>
                                        <TableCell>
                                            <Box display="flex" alignItems="center" gap={2}>
                                                <Avatar src={user.avatar} alt={user.name}>
                                                    {user.name?.charAt(0)}
                                                </Avatar>
                                                <Box>
                                                    <Typography variant="subtitle2" fontWeight={600}>
                                                        {user.name}
                                                    </Typography>
                                                    <Typography variant="caption" color="text.secondary">
                                                        {user._id}
                                                    </Typography>
                                                </Box>
                                            </Box>
                                        </TableCell>
                                        <TableCell>
                                            <Typography variant="body2">{user.email}</Typography>
                                            <Typography variant="caption" color="text.secondary">{user.phone || 'N/A'}</Typography>
                                        </TableCell>
                                        <TableCell>
                                            <Box display="flex" alignItems="center" gap={1}>
                                                <Shield size={14} color={user.role === 'admin' ? '#6366f1' : '#94a3b8'} />
                                                <Typography variant="body2" fontWeight={500} color={getRoleColor(user.role)}>
                                                    {user.role}
                                                </Typography>
                                            </Box>
                                        </TableCell>
                                        <TableCell>
                                            <Chip
                                                label={user.status || 'Active'}
                                                size="small"
                                                color={getStatusColor(user.status || 'Active') as any}
                                                variant="outlined"
                                                sx={{ fontWeight: 500, borderRadius: '6px' }}
                                            />
                                        </TableCell>
                                        <TableCell>
                                            <Typography variant="body2" color="text.secondary">
                                                {new Date(user.createdAt).toLocaleDateString()}
                                            </Typography>
                                        </TableCell>
                                        <TableCell align="right">
                                            <IconButton size="small" onClick={(e) => handleMenuOpen(e, user)}>
                                                <MoreVertical size={18} />
                                            </IconButton>
                                        </TableCell>
                                    </TableRow>
                                ))}
                                {filteredUsers.length === 0 && (
                                    <TableRow>
                                        <TableCell colSpan={6} align="center" sx={{ py: 3 }}>
                                            <Typography color="text.secondary">No users found</Typography>
                                        </TableCell>
                                    </TableRow>
                                )}
                            </TableBody>
                        </Table>
                    )}
                </TableContainer>
            </Paper>

            {/* Actions Menu */}
            <Menu
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
                onClose={handleMenuClose}
                PaperProps={{
                    elevation: 3,
                    sx: { borderRadius: '12px', minWidth: 180, mt: 1 }
                }}
                transformOrigin={{ horizontal: 'right', vertical: 'top' }}
                anchorOrigin={{ horizontal: 'right', vertical: 'bottom' }}
            >
                <MenuItem onClick={handleChangeRole}>
                    <Shield size={16} style={{ marginRight: 12 }} />
                    {selectedUser?.role === 'admin' ? 'Demote to User' : 'Promote to Admin'}
                </MenuItem>
                <Divider />
                {selectedUser?.status !== 'Blocked' ? (
                    <MenuItem onClick={() => handleUpdateUserStatus('Blocked')} sx={{ color: 'warning.main' }}>
                        <Ban size={16} style={{ marginRight: 12 }} /> Block User
                    </MenuItem>
                ) : (
                    <MenuItem onClick={() => handleUpdateUserStatus('Active')} sx={{ color: 'success.main' }}>
                        <CheckCircle size={16} style={{ marginRight: 12 }} /> Activate User
                    </MenuItem>
                )}
                <MenuItem onClick={handleDeleteUser} sx={{ color: 'error.main' }}>
                    <Trash2 size={16} style={{ marginRight: 12 }} /> Delete User
                </MenuItem>
            </Menu>

            {/* Add User Dialog */}
            <Dialog open={openAddDialog} onClose={() => setOpenAddDialog(false)} maxWidth="sm" fullWidth>
                <DialogTitle>Add New User</DialogTitle>
                <DialogContent>
                    <Box component="form" sx={{ mt: 1 }}>
                        <Stack spacing={2}>
                            <TextField
                                fullWidth
                                label="Full Name"
                                value={newUser.name}
                                onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
                                required
                            />
                            <TextField
                                fullWidth
                                label="Email Address"
                                type="email"
                                value={newUser.email}
                                onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                                required
                            />
                            <TextField
                                fullWidth
                                label="Password"
                                type="password"
                                value={newUser.password}
                                onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
                                required
                            />
                            <TextField
                                fullWidth
                                label="Phone (Optional)"
                                value={newUser.phone}
                                onChange={(e) => setNewUser({ ...newUser, phone: e.target.value })}
                            />
                        </Stack>
                    </Box>
                </DialogContent>
                <DialogActions sx={{ p: 3 }}>
                    <Button onClick={() => setOpenAddDialog(false)} disabled={actionLoading}>Cancel</Button>
                    <Button
                        variant="contained"
                        onClick={handleAddUser}
                        disabled={actionLoading}
                    >
                        {actionLoading ? 'Creating...' : 'Create User'}
                    </Button>
                </DialogActions>
            </Dialog>

            {/* Notification Toast */}
            <Snackbar
                open={toast.open}
                autoHideDuration={6000}
                onClose={() => setToast({ ...toast, open: false })}
                anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
            >
                <Alert onClose={() => setToast({ ...toast, open: false })} severity={toast.severity} sx={{ width: '100%' }}>
                    {toast.message}
                </Alert>
            </Snackbar>
        </Stack>
    );
}

'use client';

import { useState } from 'react';
import { Box, List, ListItem, ListItemButton, ListItemIcon, ListItemText, Avatar, Typography, Divider } from '@mui/material';
import {
    LayoutDashboard, FolderKanban, ShoppingBag, MessageSquare,
    FileText, User, Settings, LogOut
} from 'lucide-react';
import { useRouter } from 'next/navigation';

interface UserSidebarProps {
    currentSection: string;
    setCurrentSection: (section: string) => void;
    userName?: string;
    userEmail?: string;
    userImage?: string;
}

const SIDEBAR_ITEMS = [
    { label: 'Dashboard', icon: LayoutDashboard, id: 'dashboard' },
    { label: 'My Projects', icon: FolderKanban, id: 'projects' },
    { label: 'Services', icon: ShoppingBag, id: 'services' },
    { label: 'Messages', icon: MessageSquare, id: 'messages' },
    { label: 'Documents', icon: FileText, id: 'documents' },
    { label: 'Profile', icon: User, id: 'profile' },
    { label: 'Settings', icon: Settings, id: 'settings' },
];

export default function UserSidebar({ currentSection, setCurrentSection, userName, userEmail, userImage }: UserSidebarProps) {
    const router = useRouter();

    const handleLogout = async () => {
        await fetch('/api/auth/logout', { method: 'POST' });
        localStorage.removeItem('auth-token');
        localStorage.removeItem('user');
        router.push('/login');
    };

    return (
        <Box
            sx={{
                width: 280,
                height: '100vh',
                bgcolor: '#ffffff',
                borderRight: '1px solid #e2e8f0',
                display: 'flex',
                flexDirection: 'column',
                position: 'fixed',
                left: 0,
                top: 0,
            }}
        >
            {/* Logo/Brand */}
            <Box sx={{ p: 3, borderBottom: '1px solid #e2e8f0' }}>
                <Typography variant="h5" fontWeight="bold" sx={{
                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                }}>
                    SDEVX
                </Typography>
                <Typography variant="caption" color="text.secondary">
                    Client Portal
                </Typography>
            </Box>

            {/* User Info */}
            <Box sx={{ p: 2, borderBottom: '1px solid #e2e8f0' }}>
                <Box display="flex" alignItems="center" gap={2}>
                    <Avatar src={userImage} sx={{ bgcolor: 'primary.main', width: 48, height: 48 }}>
                        {!userImage && (userName?.charAt(0) || 'U')}
                    </Avatar>
                    <Box>
                        <Typography variant="subtitle2" fontWeight={600}>
                            {userName || 'User'}
                        </Typography>
                        <Typography variant="caption" color="text.secondary" noWrap>
                            {userEmail || 'user@example.com'}
                        </Typography>
                    </Box>
                </Box>
            </Box>

            {/* Navigation */}
            <List sx={{ flex: 1, p: 2 }}>
                {SIDEBAR_ITEMS.map((item) => {
                    const Icon = item.icon;
                    const isActive = currentSection === item.id;

                    return (
                        <ListItem key={item.id} disablePadding sx={{ mb: 0.5 }}>
                            <ListItemButton
                                onClick={() => setCurrentSection(item.id)}
                                sx={{
                                    borderRadius: '12px',
                                    bgcolor: isActive ? 'rgba(102, 126, 234, 0.1)' : 'transparent',
                                    '&:hover': {
                                        bgcolor: isActive ? 'rgba(102, 126, 234, 0.15)' : 'rgba(0, 0, 0, 0.04)',
                                    },
                                }}
                            >
                                <ListItemIcon sx={{ minWidth: 40 }}>
                                    <Icon
                                        size={20}
                                        color={isActive ? '#667eea' : '#64748b'}
                                    />
                                </ListItemIcon>
                                <ListItemText
                                    primary={item.label}
                                    primaryTypographyProps={{
                                        fontSize: 14,
                                        fontWeight: isActive ? 600 : 500,
                                        color: isActive ? '#667eea' : 'text.primary',
                                    }}
                                />
                            </ListItemButton>
                        </ListItem>
                    );
                })}
            </List>

            <Divider />

            {/* Logout */}
            <Box sx={{ p: 2 }}>
                <ListItemButton
                    onClick={handleLogout}
                    sx={{
                        borderRadius: '12px',
                        '&:hover': { bgcolor: 'rgba(239, 68, 68, 0.1)' },
                    }}
                >
                    <ListItemIcon sx={{ minWidth: 40 }}>
                        <LogOut size={20} color="#ef4444" />
                    </ListItemIcon>
                    <ListItemText
                        primary="Logout"
                        primaryTypographyProps={{
                            fontSize: 14,
                            fontWeight: 500,
                            color: 'error.main',
                        }}
                    />
                </ListItemButton>
            </Box>
        </Box>
    );
}

import { useState, useEffect } from 'react';
import { Box, Paper, Typography, Grid, Card, CardContent, LinearProgress, Chip, Stack, Button, CircularProgress } from '@mui/material';
import { FolderKanban, Clock, CheckCircle, AlertCircle, TrendingUp, MessageSquare } from 'lucide-react';

export default function DashboardOverview() {
    const [stats, setStats] = useState<any[]>([
        { label: 'Active Projects', value: '-', icon: FolderKanban, color: '#667eea', change: 'Loading...' },
        { label: 'Pending Tasks', value: '-', icon: Clock, color: '#f59e0b', change: 'Loading...' },
        { label: 'Completed', value: '-', icon: CheckCircle, color: '#10b981', change: 'Loading...' },
        { label: 'Messages', value: '-', icon: MessageSquare, color: '#3b82f6', change: 'Loading...' },
    ]);
    const [loading, setLoading] = useState(true);

    const [recentProjects, setRecentProjects] = useState<any[]>([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Fetch Stats
                const statsRes = await fetch('/api/user/stats');
                if (statsRes.ok) {
                    const data = await statsRes.json();
                    if (data.stats) {
                        const enhancedStats = data.stats.map((s: any) => ({
                            ...s,
                            icon: getIcon(s.label)
                        }));
                        setStats(enhancedStats);
                    }
                }

                // Fetch Recent Projects
                const projectsRes = await fetch('/api/user/projects');
                if (projectsRes.ok) {
                    const data = await projectsRes.json();
                    if (data.projects) {
                        setRecentProjects(data.projects.slice(0, 3));
                    }
                }
            } catch (error) {
                console.error('Error loading dashboard data:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const getIcon = (label: string) => {
        switch (label) {
            case 'Active Projects': return FolderKanban;
            case 'Pending Tasks': return Clock;
            case 'Completed': return CheckCircle;
            default: return MessageSquare;
        }
    };

    return (
        <Stack spacing={3}>
            {/* Welcome Header */}
            <Box>
                <Typography variant="h4" fontWeight="bold" gutterBottom>
                    Welcome Back! 👋
                </Typography>
                <Typography variant="body1" color="text.secondary">
                    Here's what's happening with your projects today
                </Typography>
            </Box>

            {/* Stats Cards */}
            <Grid container spacing={3}>
                {stats.map((stat, index) => {
                    const Icon = stat.icon;
                    return (
                        <Grid size={{ xs: 12, sm: 6, md: 3 }} key={index}>
                            <Card elevation={0} sx={{ border: '1px solid #e2e8f0', borderRadius: '16px' }}>
                                <CardContent>
                                    <Box display="flex" alignItems="center" justifyContent="space-between" mb={2}>
                                        <Box p={1.5} bgcolor={`${stat.color}15`} borderRadius="12px">
                                            <Icon size={24} color={stat.color} />
                                        </Box>
                                        <TrendingUp size={16} color="#10b981" />
                                    </Box>
                                    <Typography variant="h4" fontWeight="bold" gutterBottom>
                                        {stat.value}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary" gutterBottom>
                                        {stat.label}
                                    </Typography>
                                    <Typography variant="caption" color="success.main">
                                        {stat.change}
                                    </Typography>
                                </CardContent>
                            </Card>
                        </Grid>
                    );
                })}
            </Grid>

            {/* Recent Projects */}
            <Paper elevation={0} sx={{ p: 3, borderRadius: '16px', border: '1px solid #e2e8f0' }}>
                <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
                    <Typography variant="h6" fontWeight="bold">
                        Recent Projects
                    </Typography>
                    <Button variant="text" sx={{ textTransform: 'none' }}>
                        View All
                    </Button>
                </Box>

                <Stack spacing={2}>
                    {recentProjects.map((project, index) => (
                        <Box
                            key={index}
                            sx={{
                                p: 2,
                                borderRadius: '12px',
                                border: '1px solid #e2e8f0',
                                '&:hover': { bgcolor: '#f8fafc' },
                            }}
                        >
                            <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                                <Box>
                                    <Typography variant="subtitle1" fontWeight={600}>
                                        {project.name}
                                    </Typography>
                                    <Typography variant="caption" color="text.secondary">
                                        Due: {project.dueDate}
                                    </Typography>
                                </Box>
                                <Chip
                                    label={project.status}
                                    size="small"
                                    color={
                                        project.status === 'In Progress' ? 'primary' :
                                            project.status === 'Review' ? 'warning' : 'default'
                                    }
                                    sx={{ borderRadius: '6px' }}
                                />
                            </Box>
                            <Box>
                                <Box display="flex" justifyContent="space-between" mb={0.5}>
                                    <Typography variant="caption" color="text.secondary">
                                        Progress
                                    </Typography>
                                    <Typography variant="caption" fontWeight={600}>
                                        {project.progress}%
                                    </Typography>
                                </Box>
                                <LinearProgress
                                    variant="determinate"
                                    value={project.progress}
                                    sx={{
                                        height: 8,
                                        borderRadius: 4,
                                        bgcolor: '#e2e8f0',
                                        '& .MuiLinearProgress-bar': {
                                            borderRadius: 4,
                                            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                                        },
                                    }}
                                />
                            </Box>
                        </Box>
                    ))}
                </Stack>
            </Paper>

            {/* Quick Actions */}
            <Paper elevation={0} sx={{ p: 3, borderRadius: '16px', border: '1px solid #e2e8f0' }}>
                <Typography variant="h6" fontWeight="bold" mb={2}>
                    Quick Actions
                </Typography>
                <Grid container spacing={2}>
                    <Grid size={{ xs: 12, sm: 6, md: 3 }}>
                        <Button
                            fullWidth
                            variant="outlined"
