                            Send Message
                        </Button>
                    </Grid>
                    <Grid size={{ xs: 12, sm: 6, md: 3 }}>
                        <Button
                            fullWidth
                            variant="outlined"
                            sx={{
                                py: 2,
                                borderRadius: '12px',
                                textTransform: 'none',
                                borderColor: '#e2e8f0',
                            }}
                        >
                            View Invoices
                        </Button>
                    </Grid>
                </Grid>
            </Paper>
        </Stack>
    );
}

import { useState, useEffect } from 'react';
import { Box, Paper, Typography, Stack, TextField, InputAdornment, Avatar, Chip, Button, CircularProgress, Alert, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';
import { Search, Send } from 'lucide-react';

interface Message {
    _id: string;
    from: string;
    fromUserId: { _id: string, name: string, image: string };
    toUserId: { _id: string, name: string, image: string };
    subject: string;
    message: string;
    isRead: boolean;
    createdAt: string;
}

export default function MessagesSection() {
    const [messages, setMessages] = useState<Message[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [search, setSearch] = useState('');

    // Send Message Dialog
    const [openDialog, setOpenDialog] = useState(false);
    const [formData, setFormData] = useState({ subject: '', message: '' });
    const [sending, setSending] = useState(false);

    useEffect(() => {
        fetchMessages();
    }, []);

    const fetchMessages = async () => {
        try {
            setLoading(true);
            const res = await fetch('/api/user/messages');
            if (!res.ok) throw new Error('Failed to fetch messages');
            const data = await res.json();
            setMessages(data.messages || []);
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleSendMessage = async () => {
        if (!formData.subject || !formData.message) return;
        setSending(true);
        try {
            const res = await fetch('/api/user/messages', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });
            if (!res.ok) throw new Error('Failed to send message');

            await fetchMessages();
            setOpenDialog(false);
            setFormData({ subject: '', message: '' });
        } catch (err: any) {
            alert(err.message);
        } finally {
            setSending(false);
        }
    };

    const formatTime = (dateString: string) => {
        const date = new Date(dateString);
        return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    };

    const filteredMessages = messages.filter(m =>
        m.subject.toLowerCase().includes(search.toLowerCase()) ||
        (m.fromUserId?.name || 'System').toLowerCase().includes(search.toLowerCase())
    );

    return (
        <Stack spacing={3}>
            <Box display="flex" justifyContent="space-between" alignItems="center">
                <Box>
                    <Typography variant="h5" fontWeight="bold">
                        Messages
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        Communicate with the SDEVX team
                    </Typography>
                </Box>
                <Button
                    variant="contained"
                    startIcon={<Send size={18} />}
                    onClick={() => setOpenDialog(true)}
                    sx={{
                        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                        textTransform: 'none',
                        borderRadius: '12px',
                    }}
                >
                    New Message
                </Button>
            </Box>

            <TextField
                size="small"
                placeholder="Search messages..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                            <Search size={18} color="#94a3b8" />
                        </InputAdornment>
                    ),
                }}
                sx={{ maxWidth: 400, '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
            />

            {loading && <Box display="flex" justifyContent="center"><CircularProgress /></Box>}
            {error && <Alert severity="error">{error}</Alert>}

            {!loading && !error && filteredMessages.length === 0 && (
                <Paper elevation={0} sx={{ p: 4, borderRadius: '16px', border: '1px solid #e2e8f0', textAlign: 'center' }}>
                    <Typography color="text.secondary">No messages found.</Typography>
                </Paper>
            )}

            <Stack spacing={1}>
                {filteredMessages.map((msg) => (
                    <Paper
                        key={msg._id}
                        elevation={0}
                        sx={{
                            p: 2,
                            borderRadius: '12px',
                            border: '1px solid #e2e8f0',
                            bgcolor: !msg.isRead && msg.from !== 'user' ? '#f8fafc' : 'transparent',
                            cursor: 'pointer',
                            '&:hover': { bgcolor: '#f8fafc' },
                        }}
                    >
                        <Box display="flex" gap={2}>
                            <Avatar src={msg.fromUserId?.image} sx={{ bgcolor: 'primary.main' }}>
                                {msg.fromUserId?.name ? msg.fromUserId.name.charAt(0) : 'S'}
                            </Avatar>
                            <Box flex={1}>
                                <Box display="flex" justifyContent="space-between" alignItems="start" mb={0.5}>
                                    <Box>
                                        <Typography variant="subtitle2" fontWeight={600}>
                                            {msg.fromUserId?.name || 'System'}
                                        </Typography>
                                        <Typography variant="body2" fontWeight={msg.from !== 'user' && !msg.isRead ? 600 : 400}>
                                            {msg.subject}
                                        </Typography>
                                    </Box>
                                    <Box display="flex" alignItems="center" gap={1}>
                                        {msg.from !== 'user' && !msg.isRead && (
                                            <Chip
                                                label="New"
                                                size="small"
                                                color="primary"
                                                sx={{ height: 20, borderRadius: '4px' }}
                                            />
                                        )}
                                        <Typography variant="caption" color="text.secondary">
                                            {formatTime(msg.createdAt)}
                                        </Typography>
                                    </Box>
                                </Box>
                                <Typography variant="body2" color="text.secondary" noWrap>
                                    {msg.message}
                                </Typography>
                            </Box>
                        </Box>
                    </Paper>
                ))}
            </Stack>

            <Dialog
                open={openDialog}
                onClose={() => setOpenDialog(false)}
                PaperProps={{ sx: { borderRadius: '16px', maxWidth: 500, width: '100%' } }}
            >
                <DialogTitle fontWeight="bold">New Message</DialogTitle>
                <DialogContent>
                    <Stack spacing={2} mt={1}>
                        <TextField
                            label="Subject"
                            fullWidth
                            value={formData.subject}
                            onChange={(e) => setFormData({ ...formData, subject: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                        <TextField
                            label="Message"
                            fullWidth
                            multiline
                            rows={4}
                            value={formData.message}
                            onChange={(e) => setFormData({ ...formData, message: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Stack>
                </DialogContent>
                <DialogActions sx={{ p: 3 }}>
                    <Button onClick={() => setOpenDialog(false)} sx={{ borderRadius: '8px' }}>Cancel</Button>
                    <Button
                        variant="contained"
                        onClick={handleSendMessage}
                        disabled={sending}
                        sx={{ borderRadius: '8px', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}
                    >
                        {sending ? 'Sending...' : 'Send Message'}
                    </Button>
                </DialogActions>
            </Dialog>
        </Stack>
    );
}

'use client';

import { Box, Paper, Typography, Stack, TextField, Button, Avatar, Grid, Alert, Snackbar } from '@mui/material';
import { User, Mail, Phone, MapPin, Save, Building2 } from 'lucide-react';
import { useState, useEffect } from 'react';

interface ProfileData {
    name: string;
    email: string;
    phone: string;
    company: string;
    address: string;
    image: string;
}

export default function ProfileSection() {
    const [editing, setEditing] = useState(false);
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [profileData, setProfileData] = useState<ProfileData>({
        name: '',
        email: '',
        phone: '',
        company: '',
        address: '',
        image: ''
    });

    // Password change state
    const [passwordData, setPasswordData] = useState({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
    });
    const [changingPassword, setChangingPassword] = useState(false);

    // Toast notifications
    const [toast, setToast] = useState({ open: false, message: '', severity: 'success' as 'success' | 'error' });

    // Fetch profile data on mount
    useEffect(() => {
        fetchProfile();
    }, []);

    const fetchProfile = async () => {
        try {
            const res = await fetch('/api/profile', {
                credentials: 'include'
            });

            if (!res.ok) {
                throw new Error('Failed to fetch profile');
            }

            const data = await res.json();
            setProfileData({
                name: data.user.name || '',
                email: data.user.email || '',
                phone: data.user.phone || '',
                company: data.user.company || '',
                address: data.user.address || '',
                image: data.user.image || ''
            });
        } catch (error: any) {
            console.error('Profile fetch error:', error);
            setToast({ open: true, message: 'Failed to load profile', severity: 'error' });
        } finally {
            setLoading(false);
        }
    };

    const handleSaveProfile = async () => {
        if (!profileData.name.trim()) {
            setToast({ open: true, message: 'Name is required', severity: 'error' });
            return;
        }

        setSaving(true);
        try {
            const res = await fetch('/api/profile', {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                credentials: 'include',
                body: JSON.stringify({
                    name: profileData.name,
                    phone: profileData.phone,
                    company: profileData.company,
                    address: profileData.address,
                    image: profileData.image
                })
            });

            const data = await res.json();

            if (!res.ok) {
                throw new Error(data.error || 'Failed to update profile');
            }

            setToast({ open: true, message: 'Profile updated successfully!', severity: 'success' });
            setEditing(false);

            // Update localStorage user data
            const storedUser = localStorage.getItem('user');
            if (storedUser) {
                const user = JSON.parse(storedUser);
                user.name = data.user.name;
                user.image = data.user.image;
                localStorage.setItem('user', JSON.stringify(user));
                // Dispatch event to update sidebar
                window.dispatchEvent(new Event('user-updated'));
            }
        } catch (error: any) {
            console.error('Profile update error:', error);
            setToast({ open: true, message: error.message || 'Failed to update profile', severity: 'error' });
        } finally {
            setSaving(false);
        }
    };

    const handleChangePassword = async () => {
        // Validation
        if (!passwordData.currentPassword || !passwordData.newPassword || !passwordData.confirmPassword) {
            setToast({ open: true, message: 'All password fields are required', severity: 'error' });
            return;
        }

        if (passwordData.newPassword.length < 6) {
            setToast({ open: true, message: 'New password must be at least 6 characters', severity: 'error' });
            return;
        }

        if (passwordData.newPassword !== passwordData.confirmPassword) {
            setToast({ open: true, message: 'New passwords do not match', severity: 'error' });
            return;
        }

        setChangingPassword(true);
        try {
            const res = await fetch('/api/profile/password', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                credentials: 'include',
                body: JSON.stringify({
                    currentPassword: passwordData.currentPassword,
                    newPassword: passwordData.newPassword
                })
            });

            const data = await res.json();

            if (!res.ok) {
                throw new Error(data.error || 'Failed to change password');
            }

            setToast({ open: true, message: 'Password changed successfully!', severity: 'success' });
            setPasswordData({ currentPassword: '', newPassword: '', confirmPassword: '' });
        } catch (error: any) {
            console.error('Password change error:', error);
            setToast({ open: true, message: error.message || 'Failed to change password', severity: 'error' });
        } finally {
            setChangingPassword(false);
        }
    };

    const handleFileUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (!file) return;

        // Create FormData
        const formData = new FormData();
        formData.append('file', file);

        try {
            setToast({ open: true, message: 'Uploading photo...', severity: 'success' });

            const res = await fetch('/api/upload', {
                method: 'POST',
                body: formData,
                credentials: 'include'
            });

            const data = await res.json();

            if (!res.ok) {
                throw new Error(data.error || 'Upload failed');
            }

            // Update profile data with new image URL
            setProfileData(prev => ({ ...prev, image: data.imageUrl }));

            setToast({ open: true, message: 'Photo uploaded! Click Save to apply.', severity: 'success' });
        } catch (error: any) {
            console.error('Upload error:', error);
            setToast({ open: true, message: error.message || 'Failed to upload photo', severity: 'error' });
        }
    };

    if (loading) {
        return (
            <Box display="flex" alignItems="center" justifyContent="center" minHeight="400px">
                <Typography>Loading profile...</Typography>
            </Box>
        );
    }

    return (
        <Stack spacing={3}>
            <Box display="flex" justifyContent="space-between" alignItems="center">
                <Box>
                    <Typography variant="h5" fontWeight="bold">
                        Profile Settings
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        Manage your account information
                    </Typography>
                </Box>
                <Button
                    variant={editing ? 'contained' : 'outlined'}
                    startIcon={editing ? <Save size={18} /> : <User size={18} />}
                    onClick={editing ? handleSaveProfile : () => setEditing(true)}
                    disabled={saving}
                    sx={{
                        textTransform: 'none',
                        borderRadius: '12px',
                        ...(editing && {
                            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                        }),
                    }}
                >
                    {saving ? 'Saving...' : editing ? 'Save Changes' : 'Edit Profile'}
                </Button>
            </Box>

            <Paper elevation={0} sx={{ p: 3, borderRadius: '16px', border: '1px solid #e2e8f0' }}>
                <Box display="flex" alignItems="center" gap={3} mb={4}>
                    <Avatar
                        src={profileData.image}
                        sx={{
                            width: 80,
                            height: 80,
                            bgcolor: 'primary.main',
                            fontSize: 32
                        }}
                    >
                        {!profileData.image && profileData.name.charAt(0).toUpperCase()}
                    </Avatar>
                    <Box>
                        <Typography variant="h6" fontWeight="bold">
                            Profile Picture
                        </Typography>
                        <Typography variant="body2" color="text.secondary" mb={1}>
                            JPG, PNG or GIF. Max size 2MB
                        </Typography>
                        {editing && (
                            <>
                                <input
                                    accept="image/*"
                                    style={{ display: 'none' }}
                                    id="user-profile-file-input"
                                    type="file"
                                    onChange={handleFileUpload}
                                />
                                <label htmlFor="user-profile-file-input">
                                    <Button variant="outlined" component="span" size="small" sx={{ textTransform: 'none', borderRadius: '8px' }}>
                                        Upload Photo
                                    </Button>
                                </label>
                            </>
                        )}
                    </Box>
                </Box>

                <Grid container spacing={3}>
                    <Grid item xs={12} sm={6}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Full Name *
                        </Typography>
                        <TextField
                            fullWidth
                            value={profileData.name}
                            onChange={(e) => setProfileData({ ...profileData, name: e.target.value })}
                            disabled={!editing}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>

                    <Grid item xs={12} sm={6}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Email Address
                        </Typography>
                        <TextField
                            fullWidth
                            value={profileData.email}
                            disabled
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>

                    <Grid item xs={12} sm={6}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Phone Number
                        </Typography>
                        <TextField
                            fullWidth
                            value={profileData.phone}
                            onChange={(e) => setProfileData({ ...profileData, phone: e.target.value })}
                            disabled={!editing}
                            placeholder="+1 (555) 123-4567"
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>

                    <Grid item xs={12} sm={6}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Company
                        </Typography>
                        <TextField
                            fullWidth
                            value={profileData.company}
                            onChange={(e) => setProfileData({ ...profileData, company: e.target.value })}
                            disabled={!editing}
                            placeholder="Your Company"
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>

                    <Grid item xs={12}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Address
                        </Typography>
                        <TextField
                            fullWidth
                            multiline
                            rows={2}
                            value={profileData.address}
                            onChange={(e) => setProfileData({ ...profileData, address: e.target.value })}
                            disabled={!editing}
                            placeholder="Your address"
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>
                </Grid>
            </Paper>

            <Paper elevation={0} sx={{ p: 3, borderRadius: '16px', border: '1px solid #e2e8f0' }}>
                <Typography variant="h6" fontWeight="bold" mb={2}>
                    Change Password
                </Typography>
                <Grid container spacing={2}>
                    <Grid item xs={12}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Current Password
                        </Typography>
                        <TextField
                            fullWidth
                            type="password"
                            value={passwordData.currentPassword}
                            onChange={(e) => setPasswordData({ ...passwordData, currentPassword: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            New Password
                        </Typography>
                        <TextField
                            fullWidth
                            type="password"
                            value={passwordData.newPassword}
                            onChange={(e) => setPasswordData({ ...passwordData, newPassword: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <Typography variant="caption" color="text.secondary" mb={0.5} display="block">
                            Confirm Password
                        </Typography>
                        <TextField
                            fullWidth
                            type="password"
                            value={passwordData.confirmPassword}
                            onChange={(e) => setPasswordData({ ...passwordData, confirmPassword: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Grid>
                    <Grid item xs={12}>
                        <Button
                            variant="outlined"
                            onClick={handleChangePassword}
                            disabled={changingPassword}
                            sx={{ textTransform: 'none', borderRadius: '12px' }}
                        >
                            {changingPassword ? 'Updating...' : 'Update Password'}
                        </Button>
                    </Grid>
                </Grid>
            </Paper>

            {/* Toast Notification */}
            <Snackbar
                open={toast.open}
                autoHideDuration={4000}
                onClose={() => setToast({ ...toast, open: false })}
                anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
            >
                <Alert
                    onClose={() => setToast({ ...toast, open: false })}
                    severity={toast.severity}
                    sx={{ borderRadius: '12px' }}
                >
                    {toast.message}
                </Alert>
            </Snackbar>
        </Stack>
    );
}

import { useState, useEffect } from 'react';
import { Box, Paper, Typography, Stack, Chip, Button, LinearProgress, Grid, CircularProgress, Alert, Dialog, DialogTitle, DialogContent, DialogActions, TextField } from '@mui/material';
import { FolderKanban, Clock, Download, Eye, Plus } from 'lucide-react';

interface Project {
    _id: string;
    name: string;
    description: string;
    status: string;
    progress: number;
    startDate: string;
    dueDate: string;
    team: string;
    deliverables: string[];
}

export default function ProjectsSection() {
    const [projects, setProjects] = useState<Project[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Dialog State
    const [openDialog, setOpenDialog] = useState(false);
    const [requestLoading, setRequestLoading] = useState(false);
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        dueDate: '',
        type: ''
    });

    useEffect(() => {
        fetchProjects();
    }, []);

    const fetchProjects = async () => {
        try {
            setLoading(true);
            const response = await fetch('/api/user/projects', {
                credentials: 'include'
            });

            if (!response.ok) {
                throw new Error('Failed to fetch projects');
            }

            const data = await response.json();
            setProjects(data.projects || []);
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleRequestProject = async () => {
        if (!formData.name || !formData.description || !formData.dueDate) {
            alert('Please fill in all required fields');
            return;
        }

        setRequestLoading(true);
        try {
            const res = await fetch('/api/user/projects/request', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });

            if (!res.ok) {
                const data = await res.json();
                throw new Error(data.error || 'Failed to request project');
            }

            // Refresh projects
            await fetchProjects();
            setOpenDialog(false);
            setFormData({ name: '', description: '', dueDate: '', type: '' });
        } catch (err: any) {
            alert(err.message);
        } finally {
            setRequestLoading(false);
        }
    };

    const getStatusColor = (status: string) => {
        switch (status) {
            case 'In Progress': return 'primary';
            case 'Review': return 'warning';
            case 'Planning': return 'info';
            case 'Completed': return 'success';
            default: return 'default';
        }
    };

    return (
        <Stack spacing={3}>
            <Box display="flex" justifyContent="space-between" alignItems="center">
                <Box>
                    <Typography variant="h5" fontWeight="bold">
                        My Projects
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        Track and manage your ongoing projects
                    </Typography>
                </Box>
                <Button
                    variant="contained"
                    startIcon={<Plus size={18} />}
                    onClick={() => setOpenDialog(true)}
                    sx={{
                        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                        textTransform: 'none',
                        borderRadius: '12px',
                    }}
                >
                    Request New Project
                </Button>
            </Box>

            {loading && (
                <Box display="flex" justifyContent="center" p={4}>
                    <CircularProgress />
                </Box>
            )}

            {error && (
                <Alert severity="error" sx={{ borderRadius: '12px' }}>
                    {error}
                </Alert>
            )}

            {!loading && !error && projects.length === 0 && (
                <Paper elevation={0} sx={{ p: 4, borderRadius: '16px', border: '1px solid #e2e8f0', textAlign: 'center' }}>
                    <Typography color="text.secondary">
                        No projects yet. Request a new project to get started!
                    </Typography>
                </Paper>
            )}

            {!loading && !error && projects.length > 0 && (
                <Grid container spacing={3}>
                    {projects.map((project) => (
                        <Grid size={{ xs: 12 }} key={project._id}>
                            <Paper elevation={0} sx={{ p: 3, borderRadius: '16px', border: '1px solid #e2e8f0' }}>
                                <Box display="flex" justifyContent="space-between" alignItems="start" mb={2}>
                                    <Box flex={1}>
                                        <Box display="flex" alignItems="center" gap={2} mb={1}>
                                            <Typography variant="h6" fontWeight="bold">
                                                {project.name}
                                            </Typography>
                                            <Chip
                                                label={project.status}
                                                size="small"
                                                color={getStatusColor(project.status) as any}
                                                sx={{ borderRadius: '6px' }}
                                            />
                                        </Box>
                                        <Typography variant="body2" color="text.secondary" mb={2}>
                                            {project.description}
                                        </Typography>

                                        <Grid container spacing={2} mb={2}>
                                            <Grid size={{ xs: 12, sm: 6, md: 3 }}>
                                                <Typography variant="caption" color="text.secondary">
                                                    Team
                                                </Typography>
                                                <Typography variant="body2" fontWeight={500}>
                                                    {project.team}
                                                </Typography>
                                            </Grid>
                                            <Grid size={{ xs: 12, sm: 6, md: 3 }}>
                                                <Typography variant="caption" color="text.secondary">
                                                    Start Date
                                                </Typography>
                                                <Typography variant="body2" fontWeight={500}>
                                                    {new Date(project.startDate).toLocaleDateString()}
                                                </Typography>
                                            </Grid>
                                            <Grid size={{ xs: 12, sm: 6, md: 3 }}>
                                                <Typography variant="caption" color="text.secondary">
                                                    Due Date
                                                </Typography>
                                                <Typography variant="body2" fontWeight={500}>
                                                    {new Date(project.dueDate).toLocaleDateString()}
                                                </Typography>
                                            </Grid>
                                            <Grid size={{ xs: 12, sm: 6, md: 3 }}>
                                                <Typography variant="caption" color="text.secondary">
                                                    Deliverables
                                                </Typography>
                                                <Typography variant="body2" fontWeight={500}>
                                                    {project.deliverables.length} items
                                                </Typography>
                                            </Grid>
                                        </Grid>

                                        <Box mb={2}>
                                            <Box display="flex" justifyContent="space-between" mb={0.5}>
                                                <Typography variant="caption" color="text.secondary">
                                                    Progress
                                                </Typography>
                                                <Typography variant="caption" fontWeight={600}>
                                                    {project.progress}%
                                                </Typography>
                                            </Box>
                                            <LinearProgress
                                                variant="determinate"
                                                value={project.progress}
                                                sx={{
                                                    height: 8,
                                                    borderRadius: 4,
                                                    bgcolor: '#e2e8f0',
                                                    '& .MuiLinearProgress-bar': {
                                                        borderRadius: 4,
                                                        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                                                    },
                                                }}
                                            />
                                        </Box>

                                        <Stack direction="row" spacing={1} flexWrap="wrap">
                                            {project.deliverables.map((item, idx) => (
                                                <Chip
                                                    key={idx}
                                                    label={item}
                                                    size="small"
                                                    variant="outlined"
                                                    sx={{ borderRadius: '6px' }}
                                                />
                                            ))}
                                        </Stack>
                                    </Box>

                                    <Stack direction="row" spacing={1} ml={2}>
                                        <Button
                                            variant="outlined"
                                            size="small"
                                            startIcon={<Eye size={16} />}
                                            sx={{ textTransform: 'none', borderRadius: '8px' }}
                                        >
                                            View
                                        </Button>
                                        <Button
                                            variant="outlined"
                                            size="small"
                                            startIcon={<Download size={16} />}
                                            sx={{ textTransform: 'none', borderRadius: '8px' }}
                                        >
                                            Files
                                        </Button>
                                    </Stack>
                                </Box>
                            </Paper>
                        </Grid>
                    ))}
                </Grid>
            )}

            {/* Application Request Dialog */}
            <Dialog
                open={openDialog}
                onClose={() => setOpenDialog(false)}
                PaperProps={{
                    sx: { borderRadius: '16px', maxWidth: 500, width: '100%' }
                }}
            >
                <DialogTitle fontWeight="bold">Request New Project</DialogTitle>
                <DialogContent>
                    <Stack spacing={2} mt={1}>
                        <TextField
                            label="Project Name"
                            fullWidth
                            value={formData.name}
                            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                        <TextField
                            label="Description"
                            fullWidth
                            multiline
                            rows={3}
                            value={formData.description}
                            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                        <TextField
                            label="Project Type"
                            fullWidth
                            placeholder="e.g. Web Development, Mobile App"
                            value={formData.type}
                            onChange={(e) => setFormData({ ...formData, type: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                        <TextField
                            label="Target Due Date"
                            type="date"
                            fullWidth
                            InputLabelProps={{ shrink: true }}
                            value={formData.dueDate}
                            onChange={(e) => setFormData({ ...formData, dueDate: e.target.value })}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Stack>
                </DialogContent>
                <DialogActions sx={{ p: 3 }}>
                    <Button
                        onClick={() => setOpenDialog(false)}
                        sx={{ textTransform: 'none', borderRadius: '8px' }}
                    >
                        Cancel
                    </Button>
                    <Button
                        variant="contained"
                        onClick={handleRequestProject}
                        disabled={requestLoading}
                        sx={{
                            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                            textTransform: 'none',
                            borderRadius: '8px',
                        }}
                    >
                        {requestLoading ? 'Submitting...' : 'Submit Request'}
                    </Button>
                </DialogActions>
            </Dialog>
        </Stack>
    );
}

import { useState, useEffect } from 'react';
import { Box, Paper, Typography, Stack, Grid, Card, CardContent, Button, Chip, CircularProgress, Alert, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Snackbar } from '@mui/material';
import { Code, Palette, Search, Smartphone, TrendingUp, Shield } from 'lucide-react';

interface Service {
    _id: string;
    name: string;
    description: string;
    icon: string;
    color: string;
    features: string[];
    price: string;
    isActive: boolean;
}

export default function ServicesSection() {
    const [services, setServices] = useState<Service[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    // Request Dialog State
    const [selectedService, setSelectedService] = useState<Service | null>(null);
    const [openDialog, setOpenDialog] = useState(false);
    const [requestDetails, setRequestDetails] = useState('');
    const [requestLoading, setRequestLoading] = useState(false);

    // Toast
    const [toast, setToast] = useState({ open: false, message: '', severity: 'success' as 'success' | 'error' });

    useEffect(() => {
        fetchServices();
    }, []);

    const fetchServices = async () => {
        try {
            setLoading(true);
            const response = await fetch('/api/services');

            if (!response.ok) {
                throw new Error('Failed to fetch services');
            }

            const data = await response.json();
            setServices(data.services || []);
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleOpenRequest = (service: Service) => {
        setSelectedService(service);
        setOpenDialog(true);
    };

    const handleSubmitRequest = async () => {
        if (!selectedService) return;
        if (!requestDetails.trim()) {
            setToast({ open: true, message: 'Please provide some details about your needs', severity: 'error' });
            return;
        }

        setRequestLoading(true);
        try {
            const res = await fetch('/api/user/services/request', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    serviceId: selectedService._id,
                    serviceName: selectedService.name,
                    details: requestDetails
                })
            });

            const data = await res.json();

            if (!res.ok) {
                throw new Error(data.error || 'Request failed');
            }

            setToast({ open: true, message: 'Service requested successfully! We will contact you soon.', severity: 'success' });
            setOpenDialog(false);
            setRequestDetails('');
            setSelectedService(null);
        } catch (err: any) {
            setToast({ open: true, message: err.message, severity: 'error' });
        } finally {
            setRequestLoading(false);
        }
    };

    const getIcon = (iconName: string) => {
        const icons: any = { Code, Palette, Search, Smartphone, TrendingUp, Shield };
        return icons[iconName] || Code;
    };

    return (
        <Stack spacing={3}>
            <Box>
                <Typography variant="h5" fontWeight="bold" gutterBottom>
                    Our Services
                </Typography>
                <Typography variant="body2" color="text.secondary">
                    Request professional services for your business
                </Typography>
            </Box>

            {loading && (
                <Box display="flex" justifyContent="center" p={4}>
                    <CircularProgress />
                </Box>
            )}

            {error && (
                <Alert severity="error" sx={{ borderRadius: '12px' }}>
                    {error}
                </Alert>
            )}

            {!loading && !error && (
                <Grid container spacing={3}>
                    {services.map((service, index) => {
                        const Icon = getIcon(service.icon);
                        return (
                            <Grid size={{ xs: 12, sm: 6, md: 4 }} key={index}>
                                <Card
                                    elevation={0}
                                    sx={{
                                        height: '100%',
                                        border: '1px solid #e2e8f0',
                                        borderRadius: '16px',
                                        transition: 'all 0.3s',
                                        '&:hover': {
                                            boxShadow: '0 8px 24px rgba(0,0,0,0.1)',
                                            transform: 'translateY(-4px)',
                                        },
                                    }}
                                >
                                    <CardContent sx={{ p: 3 }}>
                                        <Box p={1.5} bgcolor={`${service.color}15`} borderRadius="12px" width="fit-content" mb={2}>
                                            <Icon size={28} color={service.color} />
                                        </Box>

                                        <Typography variant="h6" fontWeight="bold" gutterBottom>
                                            {service.name}
                                        </Typography>

                                        <Typography variant="body2" color="text.secondary" mb={2}>
                                            {service.description}
                                        </Typography>

                                        <Stack direction="row" spacing={0.5} flexWrap="wrap" mb={2}>
                                            {service.features.map((feature, idx) => (
                                                <Chip
                                                    key={idx}
                                                    label={feature}
                                                    size="small"
                                                    sx={{ borderRadius: '6px', mb: 0.5 }}
                                                />
                                            ))}
                                        </Stack>

                                        <Typography variant="subtitle2" fontWeight="bold" color="primary" mb={2}>
                                            {service.price}
                                        </Typography>

                                        <Button
                                            fullWidth
                                            variant="outlined"
                                            onClick={() => handleOpenRequest(service)}
                                            sx={{
                                                borderRadius: '12px',
                                                textTransform: 'none',
                                                borderColor: service.color,
                                                color: service.color,
                                                '&:hover': {
                                                    bgcolor: `${service.color}15`,
                                                    borderColor: service.color,
                                                },
                                            }}
                                        >
                                            Request Service
                                        </Button>
                                    </CardContent>
                                </Card>
                            </Grid>
                        );
                    })}
                </Grid>
            )}

            {/* Service Request Dialog */}
            <Dialog
                open={openDialog}
                onClose={() => setOpenDialog(false)}
                PaperProps={{
                    sx: { borderRadius: '16px', maxWidth: 500, width: '100%' }
                }}
            >
                <DialogTitle fontWeight="bold">
                    Request {selectedService?.name}
                </DialogTitle>
                <DialogContent>
                    <Stack spacing={2} mt={1}>
                        <Typography variant="body2" color="text.secondary">
                            Tell us a bit about your requirements. Our team will get back to you shortly.
                        </Typography>
                        <TextField
                            label="Requirements / Details"
                            fullWidth
                            multiline
                            rows={4}
                            value={requestDetails}
                            onChange={(e) => setRequestDetails(e.target.value)}
                            sx={{ '& .MuiOutlinedInput-root': { borderRadius: '12px' } }}
                        />
                    </Stack>
                </DialogContent>
                <DialogActions sx={{ p: 3 }}>
                    <Button
                        onClick={() => setOpenDialog(false)}
                        sx={{ textTransform: 'none', borderRadius: '8px' }}
                    >
                        Cancel
                    </Button>
                    <Button
                        variant="contained"
                        onClick={handleSubmitRequest}
                        disabled={requestLoading}
                        sx={{
                            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                            textTransform: 'none',
                            borderRadius: '8px',
                        }}
                    >
                        {requestLoading ? 'Submitting...' : 'Send Request'}
                    </Button>
                </DialogActions>
            </Dialog>

            {/* Toast Notification */}
            <Snackbar
                open={toast.open}
                autoHideDuration={4000}
                onClose={() => setToast({ ...toast, open: false })}
                anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
            >
                <Alert
                    onClose={() => setToast({ ...toast, open: false })}
                    severity={toast.severity}
                    sx={{ borderRadius: '12px' }}
                >
                    {toast.message}
                </Alert>
            </Snackbar>
        </Stack>
    );
}

import { SignJWT, jwtVerify } from 'jose';

const JWT_SECRET = process.env.JWT_SECRET || 'your-secret-key-change-in-production';
const SECRET_KEY = new TextEncoder().encode(JWT_SECRET);

export interface TokenPayload {
    userId: string;
    email: string;
    role: string;
    [key: string]: any; // Allow other standard JWT claims
}

export async function generateToken(payload: TokenPayload): Promise<string> {
    const { userId, email, role } = payload;
    return await new SignJWT({ userId, email, role })
        .setProtectedHeader({ alg: 'HS256' })
        .setIssuedAt()
        .setExpirationTime('7d')
        .sign(SECRET_KEY);
}

export async function verifyToken(token: string): Promise<TokenPayload | null> {
    try {
        const { payload } = await jwtVerify(token, SECRET_KEY);
        return payload as unknown as TokenPayload;
    } catch (error) {
        return null;
    }
}

import mongoose from 'mongoose';

const MONGODB_URI = process.env.MONGODB_URI || 'mongodb://localhost:27017/sdevx';

if (!MONGODB_URI) {
    throw new Error('Please define the MONGODB_URI environment variable');
}

let cached = (global as any).mongoose;

if (!cached) {
    cached = (global as any).mongoose = { conn: null, promise: null };
}

async function connectDB() {
    if (cached.conn) {
        return cached.conn;
    }

    if (!cached.promise) {
        const opts = {
            bufferCommands: false,
        };

        cached.promise = mongoose.connect(MONGODB_URI, opts).then((mongoose) => {
            return mongoose;
        });
    }

    try {
        cached.conn = await cached.promise;
    } catch (e) {
        cached.promise = null;
        throw e;
    }

    return cached.conn;
}

export default connectDB;

